const widgetOwner = props.widgetOwner || "astraplusplus.ndctools.near";
const daoId = props.daoId;

const baseApi = "https://api.pikespeak.ai";
const publicApiKey = "36f2b87a-7ee6-40d8-80b9-5e68e587a5b5";

const fetchApiConfig = {
  mode: "cors",
  headers: {
    "x-api-key": publicApiKey,
  },
};

const constructURL = (baseURL, paramObj) => {
  let params = "";
  for (const [key, value] of Object.entries(paramObj ?? {})) {
    params += `${key}=${value}&`;
  }
  params = params.slice(0, -1);
  return `${baseURL}?${params}`;
};

const fether = {
  balances: (accounts) => {
    return fetch(
      constructURL(`${baseApi}/account/balances`, { accounts }),
      fetchApiConfig
    );
  },
  near_transfers: (account, limit, offset, minamount) => {
    return fetch(
      constructURL(`${baseApi}/account/near-transfer/${account}`, {
        offset,
        limit,
        minamount,
      }),
      fetchApiConfig
    );
  },
  ft_transfers: (account, limit, offset) => {
    return fetch(
      constructURL(`${baseApi}/account/ft-transfer/${account}`, {
        offset,
        limit,
      }),
      fetchApiConfig
    );
  },
  outgoing_near: (account) => {
    return fetch(
      constructURL(`${baseApi}/account/outgoing-near/${account}`),
      fetchApiConfig
    );
  },
};

const Container = styled.div``;

const containerStyles = {
  backgroundColor: "#151718",
  color: "#fff",
};

return (
  <Container style={containerStyles} className="d-flex flex-column gap-4">
    <div className="ndc-card p-4" style={{ flex: 1, color: "WHITE" }}>
      <h3 className="mb-3">Current Balance</h3>
      <Widget
        src="abdullahi3000.near/widget/Data.Balances"
        props={{
          balances: balances.body,
        }}
      />
    </div>
