      src={`${widgetOwner}/widget/DAO.Funds.Transactions`}
      props={{ daoId, fether }}
    /> */}
    {/* <Widget
      src={`${widgetOwner}/widget/DAO.Funds.Outgoing`}
      props={{ daoId, fether }}
    /> */}
  </Container>
);
