const humanize = (str) => {
    if (!str) return "";
    return str.replace(/([A-Z])/g, " $1").replace(/^./, (s) => s.toUpperCase());
};

const genSchema = (namespace, entityType, entityTitle) => {
    const title = entityTitle || humanize(entityType);
    return {
        namespace: namespace,
        entityType: entityType,
        entityTitle: title,
        id: {
            type: "integer",
            displayType: "hidden",
        },
        accountId: {
            type: "text",
            displayType: "hidden",
        },
        name: {
            type: "string",
            inputProps: {
                min: 2,
                max: 80,
                allowCommaAndSpace: false,
                placeholder: `Choose a unique identifier for your ${title}.`,
                required: true,
            },
            label: "Name",
            order: 1,
        },
        displayName: {
            type: "string",
            inputProps: {
                min: 2,
                max: 255,
                placeholder: "The name that will be displayed to users.",
                required: true,
            },
            label: "Display Name",
            order: 2,
        },
        logoUrl: {
            type: "string",
            inputProps: {
                min: 4,
                max: 255,
                placeholder: `The logo URL for the ${title}.`,
                required: false,
            },

            label: "Logo URL",
            order: 5,
        },
        proprietary: {
            type: "string",
            inputProps: {
                min: 4,
                max: 255,
                placeholder: `Whether this is a proprietary model or open source`,
                required: false,
            },
            label: "Is proprietary",
            order: 6,
        }
    }
};

return {genSchema};
