const NavItem = styled.a`
  position: relative;
  cursor: pointer;
  padding: 0.5em;
  margin: 1.5em 0;
  font-style: normal;
  font-weight: 600;
  font-size: 0.8em;
  line-height: 1em;
  color: #3a3f42;
  border-radius: 5px;
  text-decoration: none;
  transition: background-color 0.2s ease-in-out;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 0.75em;

  @media screen and (max-width: 768px) {
    width: 100%;
    flex-direction: row;
  }

  &:hover {
    color: #667085;
    text-decoration: none;
    background-color: #f9fafb;
  }

  span {
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 16px;
    text-align: center;
  }
`;

const NavSelected = styled.div`
    color: #0d99ff;
     display: flex;
    align-items: center;
    justify-content: center;
    font-size: 16px;
    text-align: center;
`;

const navItem = ({ text, icon, id, iconSelected }) => (
  <NavItem
    href={`#/${ownerId}/widget/GenaDrop.index?tab=${id}`}
    onClick={() => {
      () => props.update({ tab: id });
    }}
  >
    {id === props.tab ? iconSelected : icon}
    {id === props.tab ? <NavSelected>{text}</NavSelected> : <span>{text}</span>}
  </NavItem>
);

const NavContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-right: 2.5em;
  gap: 0.8em;
   background: linear-gradient(180deg,#e4f1fb 0%, rgba(0,255,0,0) 3%);
  background: -webkit-linear-gradient(180deg,#e4f1fb 0%, rgba(0,255,0,0) 3%);
  background: -moz-linear-gradient(270deg,#e4f1fb 0%, rgba(0,255,0,0) 3%);

  @media screen and (max-width: 768px) {
    width: 100%;
  }
`;

const rotateLeft = styled.keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(-90deg);
  }
`;

const rotateRight = styled.keyframes`
  from {
    transform: rotate(-90deg);
  }
  to {
    transform: rotate(0deg);
  }
`;

const Trigger = styled("Collapsible.Trigger")`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  border-radius: 100px;
  padding: 0.5em;
  border: none;
  background-color: #f9fafb;
  transition: transform 0.25s ease-in-out;
  width: 40px;
  height: 40px;

  &[data-state="open"] {
    transform: rotate(-90deg);
  }

  &[data-state="closed"] {
    transform: rotate(0deg);
  }
`;

const slideRight = styled.keyframes`
  from {
    width: 0;
    height: 0;
  }
  to {
    width: var(--radix-collapsible-content-width);
    height: var(--radix-collapsible-content-height);
  }
`;

const slideLeft = styled.keyframes`
  from {
    width: var(--radix-collapsible-content-width);
    height: var(--radix-collapsible-content-height);
  }
  to {
    width: 0;
    height: 0;
  }
`;

const Content = styled("Collapsible.Content")`
  width: 100%;
  overflow: hidden;

  &[data-state="open"] {
    animation: ${slideRight} 0.25s ease-out;
  }

  &[data-state="closed"] {
    animation: ${slideLeft} 0.25s ease-out;
  }
`;

const Root = styled("Collapsible.Root")`
  dislay: flex;
  flex-direction: column;
  align-items: flex-end;
  justify-content: flex-start;

  transition: all 0.25s ease-in-out;
  gap: 1.5em;
  width: 100%;
  margin-bottom: 1.5em;

  &[data-state="open"] {
    display: flex;
  }

  &[data-state="closed"] {
    display: flex;
  }
`;

const Divider = styled.hr`
  background-color: #eceef0;
  height: 1px;
  border: none;
  width: 100%;
`;

const content = (
  <NavContainer>
    {navItem({
      text: "Create",
      id: "create",
      icon: create,
      iconSelected: createSelected,
    })}
    {navItem({
      text: "List Near NFT",
      id: "list",
      icon: list,
      iconSelected: listSelected,
    })}
    {navItem({
      text: "Near NFTS",
      id: "nearNFTs",
      icon: <Widget src="agwaze.near/widget/GenaDrop.NearLogo" />,
      iconSelected: <Widget src="agwaze.near/widget/GenaDrop.NearLogo" />,
    })}
    {navItem({
      text: "EVM NFTS",
      id: "evmNFTs",
      icon: speakers,
      iconSelected: speakers,
    })}
    <Divider />
  </NavContainer>
);

if (props.collapsible) {
  return (
    <Root>
      <Trigger>
        <svg
          width="20"
          height="14"
          viewBox="0 0 20 14"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M1 7H19M1 1H19M1 13H19"
            stroke="black"
            stroke-width="2"
            stroke-linecap="round"
            stroke-linejoin="round"
          />
        </svg>
      </Trigger>
      <Content>{content}</Content>
    </Root>
  );
}

return content;
