const StyledContainer = styled.div`
  padding-top: 34px;
  width: 1244px;
  margin: 0 auto;
`;
const StyledHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

State.init({
  tab: "market",
  chainId: -1,
});

const {
  CHAIN_LIST,
  curChain,
  dexConfig,
  wethAddress,
  multicallAddress,
  multicall,
  prices,
  onSwitchChain,
  switchingChain,
  addAction,
  toast,
  chainId,
  nativeCurrency,
  isChainSupported,
  account,
} = props;
const { type } = dexConfig;

let tabsArray;
if (type === "liquity") {
  tabsArray = [
    { key: "market", label: "Borrow" },
    { key: "yours", label: "Earn" },
  ];
} else {
  tabsArray = [
    { key: "market", label: "Market" },
    { key: "yours", label: "Yours" },
  ];
}

return (
  <StyledContainer style={dexConfig.theme}>
    <StyledHeader>
      <Widget
        src="bluebiu.near/widget/Lending.CardTabs"
        props={{
          tabs: tabsArray,
          active: state.tab,
          onChange: (tab) => {
            State.update({
              tab: tab.key,
            });
          },
        }}
      />
      <Widget
        src="bluebiu.near/widget/Lending.Chains"
        props={{
          chains: CHAIN_LIST,
          curChain,
          onSwitchChain,
          onChange: (tab) => {
            State.update({
              tab: tab.key,
            });
          },
        }}
      />
    </StyledHeader>
    {type === "liquity" ? (
      <Widget
        src="bluebiu.near/widget/Lending.LiquityContent"
        props={{
          dexConfig,
          wethAddress,
          multicallAddress,
          multicall,
          prices,
          chainIdNotSupport: !isChainSupported,
          account,
          addAction,
          toast,
          chainId,
          nativeCurrency,
          tab: state.tab,
        }}
      />
    ) : (
      <Widget
        src="bluebiu.near/widget/Lending.Content"
        props={{
          dexConfig,
          wethAddress,
          multicallAddress,
          multicall,
          prices,
          chainIdNotSupport: !isChainSupported,
          account,
          addAction,
          toast,
          chainId,
          nativeCurrency,
          tab: state.tab,
        }}
      />
    )}

    {!isChainSupported && (
      <Widget
        src="bluebiu.near/widget/Swap.ChainWarnigBox"
        props={{
          chain: curChain,
          onSwitchChain: onSwitchChain,
          switchingChain: switchingChain,
          theme: dexConfig.theme?.button,
        }}
      />
    )}
  </StyledContainer>
);
