const Participants = styled.div`
  background: white;
  display: flex;
  flex-direction: column;
  width: 392px;
  margin-top: 20px;
  padding: 16px;
  align-items: flex-start;
  gap: 16px;
  h1 {
    color: #000;
    leading-trim: both;
