          <>
            {/* <div className="text-center mx-auto">
              <div className="d-flex flex-column align-items-center gap-4 create-area">
                <div>
                  <h3 className="mb-2">
                    Deploy your own store to mint NFTs from
                  </h3>{" "}
                  <p>
                    You don't have any stores yet — let's create your first one!
                    Or refresh the page if you just deployed (could take up to 5
                    minutes).
                  </p>
                </div>
                <Widget
                  src={`bos.genadrop.near/widget/Mintbase.MbButton`}
                  props={{
                    label: "New Store",
                    onClick: createStoreHandler,
                    size: "big",
                    isDarkModeOn,
                  }}
                />
              </div>
            </div> */}
            <CardsContainer isDarkModeOn={isDarkModeOn}>
              {cardsData.map((data) => (
                <div className="card" key={data.buttonName}>
                  {data.icon ? (
                    data?.icon
                  ) : (
                    <i className="bi bi-airplane-fill icon"></i>
                  )}
                  {data.buttonLink ? (
                    <a
                      className="button"
                      href={data.buttonLink.uri}
                      target={data.buttonLink.isBlank && "_blank"}
                    >
                      {data.buttonName}
                    </a>
                  ) : (
                    <a
                      role="button"
                      className="button"
                      onClick={createStoreHandler}
                    >
                      {data.buttonName}
                    </a>
                  )}
                  <a className="tab" href={data.learnLink} target="_blank">
                    Learn more
                  </a>
                </div>
              ))}
            </CardsContainer>
          </>
        )}
      </>
      <MbModal
        open={modalIsOpen}
        setOpen={setModalIsOpen}
        topElement={
          <h4 style={{ marginRight: "8px" }}>Let's Create Your Store</h4>
        }
        isDarkModeOn={isDarkModeOn}
        onClose={null}
        topElementFirst={true}
      >
        <Widget
          src={`bos.genadrop.near/widget/Mintbase.App.Store.CreateForm`}
          props={{
            isDarkModeOn,
            onCancel: () => setModalIsOpen(false),
            setModalOpen: setModalIsOpen,
          }}
