const {
  onMetaCardImageClick,
  nftTypeIcon,
  showCreditCardIcon,
  metaCardImage,
  storeNameElement,
  minterImage,
  onMinterImageClick,
  tokenListings,
  data,
  loading,
  isDarkModeOn,
  isInStore,
  isConnected,
} = props;
const { getInputLabelFontType } = VM.require(
  "bos.genadrop.near/widget/Mintbase.components"
);
const { MbModal } = VM.require(
  "bos.genadrop.near/widget/Mintbase.components"
) || {
  MbModal: () => <></>,
};
const [modalOpen, setModalOpen] = useState(false);
const modalOpenHandler = () => {
  setModalOpen(true);
};
const MetaCard = styled.div`
  background-color: ${isDarkModeOn ? "#1F2937" : "#FFFFFF"};
  color: ${isDarkModeOn ? "#D1D5DB" : "#1F2937"};
  &:hover {
    background-color: ${isDarkModeOn ? "#111827" : "#F3F4F6"};
    color: ${isDarkModeOn ? "#F9FAFB" : "#111827"};
    scale: 1.01;
    .image {
      scale: 1.2;
    }
  }
  cursor: pointer;
  display: inline-block;
  transition: all 0.5s ease-in-out;
  width: 100%;
  border-radius: 0.375rem;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  height: fit-content;
  max-width: 354px;
`;
const Loader = styled.div`
  width: 100%;
  max-width: 500px;
  .base-card {
    display: flex;
    flex-flow: column nowrap;
    border-radius: 0.25rem;
    width: 100%;
    padding: 10px;
    flex-wrap: wrap;
    gap: 10px;
    background-color: #ffffff;
    transition-property: all;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    transition-duration: 300ms;
    transition-duration: 500ms;
    cursor: pointer;
    :hover {
      background-color: #f9fafb;
    }
  }
  .base-card:hover {
    scale: 1.01;
  }
  .loading-card-image {
    height: 120px;
  }
  .metaCardImage img {
    object-fit: cover !important;
  }
  .loader-top {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
    @keyframes pulse {
      0%,
      100% {
        opacity: 1;
      }
      50% {
        opacity: 0.5;
      }
    }
    & > div {
      border-radius: 0.25rem;
      width: 100%;
      height: 100%;
      background-color: #6b7280;
    }
  }
  .nearIcon {
    position: relative;
    top: -1px;
    margin-left: 3px;
  }
  .coverImg {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
  .extraMidLeftEl {
    width: 100% !important;
    height: 18px;
  }
  @media (max-width: 768px) {
    .base-card .p-med-90,
    .base-card .p-med-130 {
      font-size: 14px !important;
    }
    .base-card .p-small-90 {
      font-size: 12px !important;
    }
    .nearIcon {
      width: 11.5px;
    }
    .base-card .mb-tooltip svg {
      width: 16px;
    }
  }
`;
const LoaderBottom = styled.div`
  display: flex;
  flex-direction: column;
  ${"" /* justify-content: center; */}
  gap: 1.5rem;
  padding: 1.5%;
  & > div {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
    @keyframes pulse {
      0%,
      100% {
        opacity: 1;
      }
      50% {
        opacity: 0.5;
      }
    }
  }
  .loader-bottom-item-0 {
    border-radius: 0.25rem;
    width: 25%;
    height: 1rem;
    background-color: #6b7280;
  }
  .bt-it-1-1 {
    border-radius: 0.25rem;
    width: 50%;
    height: 1rem;
    background-color: #6b7280;
  }
  .bt-it-1-2,
  .bt-it-2-2 .s {
    border-radius: 0.25rem;
    width: 2rem;
    height: 1rem;
    background-color: #6b7280;
  }
  .bt-it-2-1 {
    border-radius: 9999px;
    width: 1.5rem;
    height: 1.5rem;
    background-color: #6b7280;
  }
  .bt-it-2-2 {
    display: flex;
    flex-direction: row;
    align-items: center;
  }
`;
const Header = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  img {
    object-fit: cover !important;
  }
  .nearIcon {
    position: relative;
    top: -1px;
    margin-left: 3px;
  }
  .coverImg {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
  .imageLayer {
    height: 354px !important;
    width: 354px;
    background: #aa4747;
    .image {
      transition: all 0.4s ease-in-out;
      height: 354px !important;
      width: 354px !important;
      object-fit: cover;
    }
  }
  .imgContainer {
    overflow: hidden;
    position: relative;
    border-top-left-radius: 0.375rem;
    border-top-right-radius: 0.375rem;
    width: 100%;
    height: 100%;
    @media (min-width: 1024px) {
    }
  }
  .topIconContainer {
    display: flex;
    position: absolute;
    top: 0.75rem;
    right: 0.75rem;
    z-index: 10;
    gap: 0.5rem;
    align-items: center;
  }
  @media (max-width: 768px) {
    .base-card .p-med-90,
    .base-card .p-med-130 {
      font-size: 14px !important;
    }
    .base-card .p-small-90 {
      font-size: 12px !important;
    }
    .nearIcon {
      width: 11.5px;
    }
    .base-card .mb-tooltip svg {
      width: 16px;
    }
  }
`;
const Footer = styled.div`
  padding: 24px;
  ${getInputLabelFontType("big")}
  .extraMidLeftEl {
    width: 100% !important;
    color: ${isDarkModeOn ? "#D1D5DB" : "#374151"};
  }
  .titleContainer {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    margin-bottom: 10px;
    align-items: center;
    color: ${isDarkModeOn ? "#fff" : "#000"};
  }
  .title {
    overflow: hidden;
    text-overflow: ellipsis;
    ${getInputLabelFontType("large")}
    white-space: nowrap;
    color: ${isDarkModeOn ? "#fff" : "#000"};
  }
  .minterTitle {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    color: #e5e7eb;
  }
  .minterImageContainer {
    display: inline;
    overflow: hidden;
    border-radius: 9999px;
    width: 1.5rem;
    height: 1.5rem;
    img {
      height: 100%;
      width: 100%;
      object-fit: cover;
    }
  }
`;
const {
  base_uri,
  description,
  media,
  nft_contract_id,
  title,
  token_id,
  issued_at,
  nft_contract_icon,
  nft_contract_name,
} = data;
const { price } = data?.price ?? "0.00";
const nftImage = base_uri ? `${base_uri}/${media}` : media;
if (loading)
  return (
    <div>
      <Loader>
        <li className="base-card thing">
          <div className="loader-top loading-card-image">
            <div></div>
          </div>
          <LoaderBottom>
            <div className="loader-bottom-item-0">
              <div></div>
            </div>
            <div className="loader-bottom-item-1">
              <div className="bt-it-1-1"></div>
              <div className="bt-it-1-2"></div>
            </div>
            <div className="loader-bottom-item-2">
              <div className="bt-it-2-1"></div>
              <div className="bt-it-2-2">
                <div className="s"></div>
              </div>
            </div>
          </LoaderBottom>
        </li>
      </Loader>
    </div>
  );
const modalContent = <div>Hey!</div>;
const NFTAction = styled.div`
  padding: 6px;
  background: #fff;
  color: #000;
  border-radius: 4px;
  text-transform: uppercase;
  font-size: 12px;
  line-height: 14px;
  font-weight: 600;
  display: flex;
  align-items: center;
  justify-content: center;
  .bi {
    line-height: 14px;
  }
  :hover {
    color: #4f58a3;
    background: #ebedfb;
  }
  &.dark-action {
    background: #1e2030;
    color: #fff;
    :hover {
      background: #282a3a;
      color: #c5d0ff;
    }
  }
`;
return (
  <>
    <MetaCard>
      <Header>
        <div className="imgContainer" onClick={onMetaCardImageClick}>
          {isConnected && (
            <div className="topIconContainer">
              <div>
                <NFTAction
                  className={isDarkModeOn && "dark-action"}
                  onClick={modalOpenHandler}
                >
                  <span>Sell</span>
                </NFTAction>
              </div>
              <div>
                <NFTAction
                  className={isDarkModeOn && "dark-action"}
                  onClick={dropdownToggleHandler}
                >
                  <span>
                    <i class="bi bi-three-dots"></i>
                  </span>
                </NFTAction>
              </div>
            </div>
          )}
          {showTradeIcons && (
            <div>
              <NFTAction
                className={isDarkModeOn && "dark-action"}
                onClick={dropdownToggleHandler}
              >
                <span>...</span>
              </NFTAction>
            </div>
          )}
          <div className="imageLayer">
            <img
              className="image"
              src={`https://image-cache-service-z3w7d7dnea-ew.a.run.app/thumbnail?url=${nftImage}`}
              alt={title}
              loading="lazy"
              decoding="async"
            />
          </div>
        </div>
      </Header>
      <Footer>
        <div className="extraMidLeftEl">{!isInStore && nft_contract_id}</div>
        <div className="titleContainer">
          <div className="title">
            {(title.length > 22 ? `${title.slice(0, 22)}...` : title) ??
              "--NFT Title--"}
          </div>
          <div>{price}</div>
        </div>
        <div className="minterTitle">
          {!minterImage ? (
            <div className="minterImageContainer" onClick={onMinterImageClick}>
              <img src="data:image/png;base64," />
            </div>
          ) : (
            <div></div>
          )}
          <div className=" flex flex-row items-center text-black dark:text-white space-x-8">
            <div className="p-small-90 metaCardScale">{tokenListings}</div>
          </div>
        </div>
      </Footer>
    </MetaCard>
    <MbModal
      open={modalOpen}
      setOpen={setModalOpen}
      topElement={<h4 style={{ marginRight: "8px" }}>Sell</h4>}
      isDarkModeOn={isDarkModeOn}
      onClose={null}
      topElementFirst={true}
    >
      {modalContent}
    </MbModal>
  </>
);
