const ownerId = "contribut3.near";
const apiUrl = "https://api-staging-fur7.onrender.com";
const search = props.search ?? "";

State.init({
  items: [],
  itemsIsFetched: false,
});

asyncFetch(`${apiUrl}/data/credits/applications?q=${search}`).then(
  ({ body: items }) => State.update({ items, itemsIsFetched: true }),
);

if (!state.itemsIsFetched) {
  return <>Loading...</>;
}

return (
  <Widget
    src={`${ownerId}/widget/List`}
    props={{
      filter: (accountId) => state.items.includes(accountId),
      items: state.items,
      full: true,
      createItem: (accountId) => (
        <Widget
          src={`${ownerId}/widget/Application.AdminCard`}
          props={{ accountId }}
        />
      ),
    }}
  />
);
