  query:
    "query Query($where: AttestationWhereInput) {\n  attestations(where: $where) {\n    id,\n    data,\n    recipient,\n    txid,\n    timeCreated\n  }\n}",
  variables: {
    where: {
      attester: { equals: "0x1caAb8ded4535bF42728feA90aFa7da1ac637E1E" },
    },
  },
});

