if (context.loading) {
  return "Loading";
}

const accountId = props.accountId;
const blockHeight = parseInt(props.blockHeight);

if (accountId === undefined || blockHeight === undefined) {
  return;
}

const adminContract = props.adminContract;

const admins = Near.view(adminContract, "get_admins", {});

const question = JSON.parse(
  Social.get(`${accountId}/question/main`, blockHeight) ?? "null"
);

const questionUrl = `dev-support.near/widget/DevSupport.Question.Page?accountId=${accountId}&blockHeight=${blockHeight}`;
const shareUrl = `https://alpha.near.org/#/${questionUrl}`;

const H2 = styled.h2`
  font-size: 20px;
  font-weight: 600;
  color: #11181C;
  }
`;

const H4 = styled.h4`
  font-size: 14px;
  font-weight: 500;
  color: #687076;

  a {
    color: inherit;
    transition: color .15s ease;
    &:hover {
      color: #30A46C;
      text-decoration: none;
    }

    & i {
      transition: color .1s ease-out;
      color: inherit;
    }
  }
`;
const H6 = styled.h6`
  font-size: 12px;
  font-weight: 600;
  text-transform: uppercase;
  color: #687076;
`;
const SidebarWrapper = styled.div`
  border-left: 1px solid #ECEEF0;

  @media(max-width: 768px) {
    border-left: none;
  }
`;
const ShareButton = styled.button`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  padding: 8px 25px;
  height: 40px;
  border-radius: 100px;
  font-weight: 600;
  font-size: 12px;
  text-align: center;
  cursor: pointer;
  background: #FBFCFD;
  border: 1px solid #D7DBDF;
  color: #006ADC !important;
  white-space: nowrap;

  &:hover,
  &:focus {
    background: #ECEDEE;
    text-decoration: none;
    outline: none;
  }

  i {
    color: #7E868C;
  }

  .bi-16 {
    font-size: 16px;
  }
`;

return (
  <div className="pt-2 pb-5">
    <H4>
      <a href="/#/dev-support.near/widget/DevSupport.Main">
        <i class="bi bi-arrow-left me-2" />
        Go back
      </a>
    </H4>

    <div class="row mt-5">
      <div class="col-md-8 col-12 pe-md-5">
        <Widget
          src="dev-support.near/widget/DevSupport.Question.PreviewDetailed"
          props={{
            accountId,
            blockHeight,
            admins,
            adminContract,
            question,
          }}
        />
      </div>
      <SidebarWrapper className="col-md-4 col-12 ps-md-5 pt-md-0 pt-5 border-md-0">
        <Widget
          src="dmitriy_sheleg.near/widget/AccountProfileCard"
          props={{ accountId }}
        />
        <H6 className="pt-5 pb-3">share</H6>

        <OverlayTrigger
          placement="top"
          overlay={<Tooltip>Copy URL to clipboard</Tooltip>}
        >
          <ShareButton
            className="share-url"
            type="button"
            onMouseLeave={() => {
              State.update({ copiedShareUrl: false });
            }}
            onClick={() => {
              clipboard.writeText(shareUrl).then(() => {
                State.update({ copiedShareUrl: true });
              });
            }}
          >
            {state.copiedShareUrl ? (
              <i className="bi-16 bi bi-check"></i>
            ) : (
              <i className="bi-16 bi-link-45deg"></i>
            )}
          </ShareButton>
        </OverlayTrigger>
      </SidebarWrapper>
    </div>
  </div>
);
