const colors = {
  brightRed: "#ff3d00",
  deepBlue: "#2835f8",
  lightBeige: "#f4e9e1",
  slateBlack: "#2B2B2B",
};

const Block = styled.div`
  align-items: center;
  border: 1px solid ${colors.slateBlack};
  border-radius: 16px;
  margin: 20px 0px;
  padding: 3vw;
  position: relative;
  width: 100%;
`;

const Container = styled.div`
  background-color: #f4e9e1;
  font-family: sans-serif;
  height: 160%;
  padding: 20px;
`;

const SymbolLabel = styled.div`
  color: ${colors.deepBlue};
  display: flex;
  justify-content: flex-end;
  padding-right: 5px;
  margin-top: 80px;
  width: 100%;
`;

const SmallText = styled.div`
  color: ${colors.slateBlack};
  font-size: 1.5vw;
  line-height: 190%;
`;

const TitleText = styled.h1`
  color: ${colors.brightRed};
  font-size: 6vw;
`;

State.init({
  selectedCoin: null,
});

const handleOptionClick = (selectedCoin) => {
  State.update({ selectedCoin });
};

return (
  <Container>
    <Widget
      props={{ handleOptionClick }}
      src={`asiyaasha.near/widget/coinSelector`}
    />
    <Block>
      {state.selectedCoin && (
        <div>
          <TitleText>{state.selectedCoin.name}</TitleText>
          <SmallText>
            Current Price: ${state.selectedCoin.current_price}
          </SmallText>
          <SmallText>Market Cap: ${state.selectedCoin.market_cap}</SmallText>
          <SmallText>Volume: ${state.selectedCoin.total_volume}</SmallText>
        </div>
      )}
      <SymbolLabel> / {state.selectedCoin.symbol}</SymbolLabel>
    </Block>
  </Container>
);
