const contract = "donate.eamondang.near";
const total = Near.view(contract, "total", {});
const tokenDecimals = 18;
const amount = 0;

if (!total || context.loading) {
  return "Loading...";
}

// Use and manipulate state
State.init({ amount });

const onInputChange = ({ target }) => {
  State.update({ amount: target.value });
};

const onBtnClick = () => {
  Near.call(contract, "donate", {
    amount: state.amount.pow(18),
  });
};

// Define components
const DonateForm = (
  <>
    <div class="border border-black p-3">
      <label>Amount Donate</label>
      <input placeholder="1000" onChange={onInputChange} />
      <button class="btn btn-primary mt-2" onClick={onBtnClick}>
        Donate
      </button>
    </div>
  </>
);

const notLoggedInWarning = <p class="text-center py-2"> Login to Donate </p>;

// Render
return (
  <>
    <div class="container border border-info p-3">
      <h3 class="text-center">
        The contract says i have:
        <span class="text-decoration-underline">{total}</span> Donate
      </h3>

      <p class="text-center py-2">Donate Me =))</p>

      {context.accountId ? DonateForm : notLoggedInWarning}
    </div>
  </>
);
