const { candidateId } = props;

const electionContract = election_contract ?? "elections-v1.gwg-testing.near";
const apiKey = api_key ?? "36f2b87a-7ee6-40d8-80b9-5e68e587a5b5";

State.init({
  voters: [],
});

asyncFetch(
  `https://api.pikespeak.ai/election/votes-by-candidate?contract=${electionContract}&candidate=${candidateId}`,
  { headers: { "x-api-key": apiKey } }
).then((resp) => {
  State.update({ voters: resp.body });
});

const VotersContainer = styled.div`
  padding: 5px 0;
`;

const Bookmark = styled.div`
  width: 100px;

  @media (max-width: 400px) {
    width: 0;
  }
`;

const VoterItem = styled.div`
  font-size: 14px;
  padding: 0 20px;
  height: 44px;
  border-bottom: 1px solid #d0d6d9;

  &:last-child {
    border: 0;
  }
`;

const StyledLink = styled.a`
  color: inherit !important;
  width: 100px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const TxnSection = styled.div`
  white-space: nowrap;
  overflow: hidden;
`;

const TxnLink = styled.a`
  color: inherit !important;
  width: 310px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const UserLink = ({ title, src }) => (
  <>
    <StyledLink href={src}>{title}</StyledLink>
    <span>
      <i class="bi bi-arrow-up-right" />
    </span>
  </>
);

return (
  <VotersContainer>
    {state.voters.map((voter) => (
      <VoterItem className="d-flex align-items-center gap-2 justify-content-between">
        <div className="d-flex align-items-center">
          <Bookmark />
          <Widget
            src="mob.near/widget/ProfileImage"
            props={{
              accountId: voter.voter,
              imageClassName: "rounded-circle w-100 h-100",
              style: { width: "24px", height: "24px", marginRight: 4 },
            }}
          />
          <UserLink
            src={`https://www.near.org/near/widget/ProfilePage?accountId=${voter.voter}`}
            title={voter.voter}
          />
        </div>
        <TxnSection className="d-flex align-items-center">
          <TxnLink
            role="button"
            target="_blank"
            href={`https://explorer.mainnet.near.org/transactions/${voter.transaction_id}`}
          >
            {voter.transaction_id}
          </TxnLink>
          <span>
            <i class="bi bi-arrow-up-right" />
          </span>
        </TxnSection>
      </VoterItem>
    ))}
  </VotersContainer>
);
