const type = props.type;

if (!type) {
  return "props.type is not defined";
}

const index = {
  action: "thing",
  key: "main",
  options: {
    limit: 10,
    order: "desc",
    accountId: props.accounts,
  },
};

const renderThing = (a) => {
  return a.value.type === props.type && <div>{JSON.stringify(a)}</div>;
};

// <div key={JSON.stringify(a)} className="mb-3">
