const tagsPattern = "*/profile/tags/*";
const placeholder = props.placeholder ?? "Tags";
const initialTagsObject = props.initialTagsObject || {};

const sayALotWorkers = [
  "silkking.near",
  "f2bc8abdb8ba64fe5aac9689ded9491ff0e6fdcd7a5c680b7cf364142d1789fb",
  "ayelen.near",
  "kenrou-it.near",
];

const data = {
  index: {
    test_sayALotTags: JSON.stringify({
      key: "main",
      value: { tags: { testTag: true } },
    }),
  },
};

// Social.set(data, {
//   force: true,
//   onCommit: () => {
//     console.log("Commit compleate");
//   },
// });

const tagsObject = JSON.parse(
  Social.get(
    `f2bc8abdb8ba64fe5aac9689ded9491ff0e6fdcd7a5c680b7cf364142d1789fb/index/test_sayALotTags`
  )
).value;

const normalizeTag = (tag) =>
  tag
    .replaceAll(/[- \.]/g, "_")
    .replaceAll(/[^\w]+/g, "")
    .replaceAll(/_+/g, "-")
    .replace(/^-+/, "")
    .replace(/-+$/, "")
    .toLowerCase()
    .trim("-");

const tagsCount = {};

const processTagsObject = (obj) => {
  Object.entries(obj).forEach((kv) => {
    if (typeof kv[1] === "object") {
      processTagsObject(kv[1]);
    } else {
      const tag = normalizeTag(kv[0]);
      tagsCount[tag] = (tagsCount[tag] || 0) + 1;
    }
  });
};

const getTags = () => {
  processTagsObject(tagsObject.tags);
  const tags = Object.entries(tagsCount);
  tags.sort((a, b) => b[1] - a[1]);
  return tags.map((t) => ({
    name: t[0],
    count: t[1],
  }));
};

if (!state.allTags) {
  initState({
    allTags: getTags(),
    tags: Object.keys(initialTagsObject).map((tag) => ({
      name: normalizeTag(tag),
    })),
    originalTags: Object.fromEntries(
      Object.keys(initialTagsObject).map((tag) => [tag, null])
    ),
    id: `tags-selector-${Date.now()}`,
  });
}

const setTags = (tags) => {
  tags = tags.map((o) => {
    o.name = normalizeTag(o.name);
    return o;
  });
  State.update({ tags });
  if (props.setTagsObject) {
    props.setTagsObject(
      Object.assign(
        {},
        state.originalTags,
        Object.fromEntries(tags.map((tag) => [tag.name, ""]))
      )
    );
  }
};

return (
  <>
    {sayALotWorkers.includes(context.accountId) && (
      <input value={state.newTags} />
    )}
    <Typeahead
      id={state.id}
      multiple
      labelKey="name"
      onChange={setTags}
      options={state.allTags}
      placeholder={placeholder}
      selected={state.tags}
      positionFixed
      allowNew
    />
    {props.debug && (
      <div>
        Debugging tags:
        <pre>{JSON.stringify(state.tags)}</pre>
      </div>
    )}
  </>
);
