const Marquee = styled.div`
  /* Styles from HTML classes */
  display: flex;
  align-items: center;

  /* Animation keyframes */
  @keyframes marquee {
    from {
      transform: translateX(0%);
    }
    to {
      transform: translateX(-100%);
    }
  }

  @keyframes marquee2 {
    from {
      transform: translateX(100%);
    }
    to {
      transform: translateX(0%);
    }
  }

  /* Animation properties */
  animation: ${({ animation }) =>
    animation === "marquee" ? animation : "marquee2"} 25s linear infinite;
`;

const Separator = styled.span`
  /* Styles from hidden class */
  display: none;

  @media (min-width: 768px) {
    /* Show on larger screens */
    display: inline;
  }
`;

const [animation, setAnimation] = useState("marquee");

const handleSwitchAnimation = () => {
  setAnimation(animation === "marquee" ? "marquee2" : "marquee");
};

return (
  <>
    <button onClick={handleSwitchAnimation}>Change Animation</button>
    <Marquee animation={animation}>
      <span>Actriz</span>
      <Separator>✺</Separator>
      <span>Bailarina</span>
      <Separator></Separator>
      <span>Peli Roja</span>
      <Separator>TTTT</Separator>
      <span>Teatro</span>
      <Separator></Separator>
      <span>Arte</span>
      <Separator></Separator>
      <span>Pivona</span>
    </Marquee>
  </>
);
