const profileData = props.profileData || [];
const [name, setName] = useState("");
const [accountId, setAccountId] = useState("");
const [image, setImage] = useState("");
const [description, setDescription] = useState("");
const [tags, setTags] = useState([]);
const [teams, setTeams] = useState([]);
const [height, setHeight] = useState("100px");
const [width, setWidth] = useState("100px");
const hanleClick = (data, accountId) => {
  if (data.accountId == accountId) {
    setAccountId(accountId);
    setName(data.data.name);
    setImage(data.data.image.ipfs_cid || data.data.image.url);
    setDescription(data.data.description);
    console.log(data.data.team);
    if (data.data.tags) {
      setTags(Object.keys(data.data.tags).flat());
    }
    if (data.data.team) {
      setTeams(Object.keys(data.data.team).flat());
    } else {
      setTeams([]);
    }
  }
};
const Header = styled.div`
  display:flex;
  flex-direction:row;
  align-items:top;
  justify-content:space-between;
  gap:10px;
  padding:10px 15px;
  .navLeft{
    display:flex;
    gap:10px;
    flex-direction:row;
  }
  .title{
    display:flex;
    flex-direction:column;
    gap:2px;
  }
  .id{
    font-size:14px;
  }
`;
const Description = styled.div`
  display:flex;
  flex-direction:column;
  gap:15px;
  padding:10px 15px;
  font-size:16px;
  .title{
    font-size:18px;
    font-weight:700;
  }
  .desc{
    background-image: linear-gradient(to right top, #cad5fe, #ced9fe, #d3ddff, #d7e0ff, #dce4ff);
    padding: 10px 15px;
    border-radius:10px;
  }
  .tags{
    background-image: linear-gradient(to right top, #cad5fe, #ced9fe, #d3ddff, #d7e0ff, #dce4ff);
    padding: 10px 15px;
    border-radius:10px;
    display:flex;
    flex-direction:row;
    gap:20px;
    @media screen and (max-width:768px){
      flex-wrap:wrap;
    }
  }
  .tag{
    font-weight:600;
    border:1px solid white;
    padding:5px 10px;
    box-shadow: 2px 2px white;
    max-height:50px;
  }
  .teams{
    background-image: linear-gradient(to right top, #cad5fe, #ced9fe, #d3ddff, #d7e0ff, #dce4ff);
    padding: 10px 10px;
    border-radius:15px;
    display:flex;
    flex-direction:row;
    justify-content:center;
    gap:20px;
    @media screen and (max-width:768px){
      ${setHeight("50px")}
      ${setWidth("50px")}
    }
  }
  .team{
    font-weight:600;
    border: 2px solid white;
    padding:5px 10px;
    border-radius:5px;
    box-shadow: 0px 2px white;
    max-height:50px;
    @media screen and (max-width:768px){
      font-size:13px;
    }
  }
  .social{
    background-image: linear-gradient(to right top, #cad5fe, #ced9fe, #d3ddff, #d7e0ff, #dce4ff);
    padding: 10px 15px;
    border-radius:10px;
    display:flex;
    flex-direction:row;
    gap:20px;
    @media screen and (max-width:768px){
      flex-wrap:wrap;
    }
  }
  .sc{
    background: #9aa8fb;
    padding:3px;
  }
`;
return (
  <>
    {profileData.length > 0 &&
      profileData.map((dt) => (
        <div
          class="item"
          data-bs-toggle="modal"
          data-bs-target="#staticBackdrop"
          onClick={() => hanleClick(dt, dt.accountId)}
        >
          {dt.data.image.url ? (
            <img class="image" src={dt.data.image.url} alt="profile" />
          ) : (
            <img
              class="image"
              src={`https://ipfs.near.social/ipfs/` + dt.data.image.ipfs_cid}
              alt="profile"
            />
          )}
          <div class="project">
            <div class="title">{dt.data.name}</div>
            <div class="decs">
              {dt.data.description.length > 80
                ? dt.data.description.slice(0, 80) + "..."
                : dt.data.description}
            </div>
          </div>
        </div>
      ))}
    <div
      class="modal fade"
      id="staticBackdrop"
      data-bs-backdrop="static"
      data-bs-keyboard="false"
      tabindex="-1"
      aria-labelledby="staticBackdropLabel"
      aria-hidden="true"
      style={{ padding: "10px 15px" }}
    >
      <div class="modal-dialog">
        <div class="modal-content">
          <Header>
            <div class="navLeft">
              {image.startsWith("https") ? (
                <img class="image" src={image} alt="profile" />
              ) : (
                <img
                  class="image"
                  src={`https://ipfs.near.social/ipfs/` + image}
                  alt="profile"
                />
              )}
              <div class="title">
                <h5 class="modal-title" id="staticBackdropLabel">
                  {name}
                </h5>
                <div class="id">@{accountId}</div>
              </div>
            </div>
            <div class="navRight">
              <button
                type="button"
                class="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
          </Header>
          <Description class="modal-body">
            <div class="title">Overview</div>
            <div class="desc">{description}</div>
            <div class="title">Tags</div>
            <div class="tags">
              {tags.map((tag) => (
                <div class="tag">{tag}</div>
              ))}
            </div>
            <div class="title">Team Member</div>
            <div class="teams">
              {teams.map((team) => (
                <div class="d-flex justify-content-center">
                  <Widget
                    src="mob.near/widget/ProfileImage"
                    props={{
                      accountId: team,
                      style: {
                        width: width,
                        height: height,
                        border: `4px #dd3345 solid`,
                        borderRadius: "50%",
                      },
                      className: "mb-2",
                      imageClassName: "rounded-circle w-100 h-100 d-block",
                      thumbnail: false,
                      tooltip: true,
                    }}
                  />
                  <div class="team">@{team}</div>
                </div>
              ))}
            </div>
            <div class="title">Social</div>
          </Description>
          <div class="modal-footer">
            <button
              type="button"
              class="btn btn-secondary"
              data-bs-dismiss="modal"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>{" "}
  </>
);
