if (!context.accountId) {
  return (
    <Widget
      src="nearhorizon.near/widget/InfoSegment"
      props={{
        title: "Not logged in!",
        description: "Connect a NEAR account to create a new page.",
      }}
    />
  );
}

const daos = Near.view("sputnik-dao.near", "get_dao_list");

if (daos === null) {
  return "Loading...";
}

const groups = [`${context.accountId}`];

State.init({
  name: "",
  nameError: "",
  pageType: [],
  pageTypeError: "",
  language: null,
  languageError: "",
  tagline: "",
  taglineError: "",
  description: "",
  descriptionError: "",
  tags: [],
  tagsError: "",
  website: "",
  websiteError: "",
  geo: "",
  geoError: "",
  members: null,
  membersError: "",
  accountsWithPermissions: [],
  accountsWithPermissionsIsFetched: false,
  oss: null,
  ossError: "",
  isValid: false,
  isAvailable: true,
  args: {
    config: {
      name: state.name,
      purpose: state.tagline,
      metadata: "",
    },
    policy: {
      groups,
    },
  },
});

if (!state.accountsWithPermissionsIsFetched) {
  Near.asyncView(
    "social.near",
    "debug_get_permissions",
    { account_id: context.accountId },
    "final",
    false
  ).then((data) =>
    State.update({
      accountsWithPermissions: data
        .map(([info]) => info)
        .filter((info) => "AccountId" in info)
        .map(({ AccountId }) => AccountId),
      accountsWithPermissionsIsFetched: true,
    })
  );
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  gap: 3em;
  padding-bottom: 3em;
`;

const Header = styled.h1`
  font-style: normal;
  font-weight: 700;
  font-size: 2em;
  line-height: 1.4em;
  text-align: center;
  color: #000000;
`;

const SubHeader = styled.h2`
  font-style: normal;
  font-weight: 400;
  font-size: 0.95em;
  line-height: 1.25em;
  text-align: center;
  color: #101828;
`;

const Form = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
  width: 60%;
  gap: 1em;
`;

const FormHeader = styled.h3`
  box-sizing: border-box;
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  padding: 0px 0px 0.5em;
  border-bottom: 1px solid #eceef0;
  font-style: normal;
  font-weight: 700;
  font-size: 1.125em;
  line-height: 1.25em;
  color: #000000;
  width: 100%;
`;

const FormFooter = styled.div`
  display: flex;
  flex-direction: row-reverse;
  align-items: center;
  justify-content: space-between;
  width: 100%;
`;

const slideDown = styled.keyframes`
  from {
    height: 0;
  }
  to {
    height: var(--radix-collapsible-content-height);
  }
`;

const slideUp = styled.keyframes`
  from {
    height: var(--radix-collapsible-content-height);
  }
  to {
    height: 0;
  }
`;

const Hidable = styled("Collapsible.Content")`
  overflow: hidden;

  &[data-state="open"] {
    animation: ${slideDown} 0.3s ease-in-out;
  }

  &[data-state="closed"] {
    animation: ${slideUp} 0.3s ease-in-out;
  }
`;

const Label = styled.label`
  align-items: flex-start;
  justify-content: flex-start;
  font-style: normal;
  font-weight: 600;
  font-size: 0.95em;
  line-height: 1.25em;
  color: #344054;
`;

const validateForm = () => {
  return (
    state.name &&
    state.nameError === "" &&
    state.accountId &&
    state.accountIdError === "" &&
    state.pageType &&
    state.pageTypeError === "" &&
    state.language &&
    state.languageError === "" &&
    (!state.tagline || state.taglineError === "") &&
    (!state.description || state.descriptionError === "") &&
    (!state.tags || state.tagsError === "") &&
    (!state.website || state.websiteError === "") &&
    (!state.geo || state.geoError === "") &&
    (!state.members || state.membersError === "") &&
    (!state.oss || state.ossError === "")
  );
};

const onChangeName = (name) => {
  State.update({
    name,
  });
};

const checkValidity = (name) => {
  if (name.length > 2) {
    return State.update({ isValid: true });
  }
};

const validName = checkValidity(state.name);

const daoId = state.name + ".sputnik-dao.near";

const policy = Near.view(daoId, "get_policy");

const deposit = policy.proposal_bond;

const checkAvailability = (daos) => {
  if (daos.indexOf(daoId) !== -1) {
    return State.update({ isAvailable: false });
  }
};

const availableName = checkAvailability(daos);

const dao_args = Buffer.from(JSON.stringify(state.args), "utf-8").toString(
  "base64"
);

const handleCreate = () => {
  Near.call([
    {
      contractName: "sputnik-dao.near",
      methodName: "create",
      args: {
        name: state.name,
        args: dao_args,
      },
      deposit: "7000000000000000000000000",
      gas: "280000000000000",
    },
  ]);
};

return (
  <Container>
    <div>
      <Header>page maker</Header>
    </div>
    <Form>
      <FormHeader>input details below</FormHeader>
      <Label>name</Label>
      <input
        label="name"
        placeholder="example"
        type="text"
        value={state.name}
        onChange={(e) => onChangeName(e.target.value)}
      ></input>
      <div className="mb-3">
        {validName ? (
          <div>
            {availableName ? (
              <p className="text-secondary">occupied</p>
            ) : (
              <p className="text-success">available</p>
            )}
          </div>
        ) : (
          <div>
            {state.name ? (
              <p className="text-secondary">not enough characters</p>
            ) : (
              ""
            )}
          </div>
        )}
      </div>
      {validName && (
        <>
          <Widget
            src="hack.near/widget/form.text"
            props={{
              label: "tagline",
              placeholder: "summary",
              value: state.tagline,
              onChange: (tagline) => State.update({ tagline }),
              validate: () => {
                if (state.tagline.length > 50) {
                  State.update({
                    taglineError: "Tagline must be less than 50 characters",
                  });
                  return;
                }

                State.update({ taglineError: "" });
              },
              error: state.taglineError,
            }}
          />
          <Widget
            src="hack.near/widget/form.text.area"
            props={{
              label: "description",
              placeholder: "about",
              value: state.description,
              onChange: (description) => State.update({ description }),
              validate: () => {
                if (state.description.length > 500) {
                  State.update({
                    descriptionError:
                      "Description must be less than 500 characters",
                  });
                  return;
                }

                State.update({ descriptionError: "" });
              },
              error: state.descriptionError,
            }}
          />
          <Widget
            src="hack.near/widget/form.select"
            props={{
              label: "tags",
              placeholder: "dev",
              options: [
                { name: "dev" },
                { name: "edu" },
                { name: "art" },
                { name: "sci" },
                { name: "gov" },
              ],
              value: state.tags,
              onChange: (tags) =>
                State.update({
                  tags: tags.map(({ name }) => ({
                    name: name.trim().replaceAll(/\s+/g, "-"),
                  })),
                }),
            }}
          />
          <Widget
            src="hack.near/widget/form.text"
            props={{
              label: "website",
              placeholder: "https://near.org",
              value: state.website,
              onChange: (website) => State.update({ website }),
              validate: () => {
                if (state.website.length > 50) {
                  State.update({
                    websiteError: "URL must be less than 50 characters",
                  });
                  return;
                }

                State.update({ websiteError: "" });
              },
            }}
          />
          <Widget
            src="hack.near/widget/form.number"
            props={{
              label: "members",
              placeholder: 10,
              value: state.members,
              onChange: (members) => State.update({ members }),
              validate: () => {
                if (state.members < 1) {
                  State.update({
                    membersError: "must be at least 1",
                  });
                  return;
                }

                State.update({ membersError: "" });
              },
            }}
          />
          <Widget
            src="hack.near/widget/form.text"
            props={{
              label: "region",
              placeholder: "where",
              value: state.geo,
              onChange: (geo) => State.update({ geo }),
              validate: () => {
                if (state.geo.length > 100) {
                  State.update({
                    geoError: "Location must be less than 100 characters",
                  });
                  return;
                }

                State.update({ geoError: "" });
              },
            }}
          />
        </>
      )}
      {validName && (
        <FormFooter>
          <Widget
            src="nearhorizon.near/widget/Buttons.Green"
            props={{
              onClick: () => {
                const data = {
                  [context.accountId]: {
                    page: {
                      name: state.name,
                      ...(state.members ? { members: `${state.members}` } : {}),
                      ...(state.tagline ? { tagline: state.tagline } : {}),
                      ...(state.description
                        ? { description: state.description }
                        : {}),
                      ...(state.tags.length
                        ? {
                            tags: state.tags.reduce(
                              (acc, { name }) =>
                                Object.assign(acc, { [name]: "" }),
                              {}
                            ),
                          }
                        : {}),
                      ...(state.website || state.socials
                        ? {
                            ...state.socials,
                            ...(state.website
                              ? {
                                  website: state.website.startsWith("http://")
                                    ? state.website.substring(7)
                                    : state.website.startsWith("https://")
                                    ? state.website.substring(8)
                                    : state.website,
                                }
                              : {}),
                          }
                        : {}),
                      language: state.language.value,
                      ...(state.geo ? { geo: state.geo } : {}),
                    },
                  },
                };
                const transactions = [
                  {
                    contractName: "social.near",
                    methodName: "set",
                    deposit,
                    args: { data },
                  },
                  {
                    contractName: "sputnik-dao.near",
                    methodName: "create",
                    args: dao_args,
                    deposit: "7000000000000000000000000",
                  },
                ];
                Near.call(transactions);
              },
              text: (
                <>
                  <svg
                    width="17"
                    height="18"
                    viewBox="0 0 17 18"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      d="M2.875 16.5V12.75M2.875 5.25V1.5M1 3.375H4.75M1 14.625H4.75M9.25 2.25L7.94937 5.63165C7.73786 6.18157 7.6321 6.45653 7.46765 6.68781C7.32189 6.8928 7.1428 7.07189 6.93781 7.21765C6.70653 7.3821 6.43157 7.48786 5.88165 7.69937L2.5 9L5.88165 10.3006C6.43157 10.5121 6.70653 10.6179 6.93781 10.7824C7.1428 10.9281 7.32189 11.1072 7.46765 11.3122C7.6321 11.5435 7.73786 11.8184 7.94937 12.3684L9.25 15.75L10.5506 12.3684C10.7621 11.8184 10.8679 11.5435 11.0324 11.3122C11.1781 11.1072 11.3572 10.9281 11.5622 10.7824C11.7935 10.6179 12.0684 10.5121 12.6184 10.3006L16 9L12.6184 7.69937C12.0684 7.48786 11.7935 7.3821 11.5622 7.21765C11.3572 7.07189 11.1781 6.8928 11.0324 6.68781C10.8679 6.45653 10.7621 6.18157 10.5506 5.63165L9.25 2.25Z"
                      stroke="#11181C"
                      stroke-width="1.66667"
                      stroke-linecap="round"
                      stroke-linejoin="round"
                    />
                  </svg>
                  create
                </>
              ),
            }}
          />
        </FormFooter>
      )}
    </Form>
  </Container>
);
