const accountId = props.accountId ?? "hack.near";
const widgetName = props.widgetName ?? "widgets.rank";

if (!accountId) {
  return "";
}

let followers = Social.keys(
  `*/graph/star/widget/${accountId}/${widgetName}`,
  "final",
  {
    return_type: "BlockHeight",
    values_only: true,
  }
);

if (followers === null) {
  return "Loading";
}

followers = Object.entries(followers || {});
followers.sort(
  (a, b) => b.graph.follow[accountId][1] - a.graph.follow[accountId][1]
);

const Wrapper = styled.div`
  display: grid;
  gap: 24px;
`;

const Item = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 12px;
`;

const Text = styled.p`
  margin: 0;
  font-size: 14px;
  line-height: 20px;
  color: ${(p) => (p.bold ? "#11181C" : "#687076")};
  font-weight: ${(p) => (p.bold ? "600" : "400")};
  font-size: ${(p) => (p.small ? "12px" : "14px")};
`;

if (followers !== null && followers.length === 0) {
  return <Text>This account doesn&apos;t have any followers yet.</Text>;
}

return (
  <Wrapper>
    {followers.map(([accountId], i) => (
      <Item key={i}>
        <Widget src="near/widget/AccountProfile" props={{ accountId }} />
        <Widget src="near/widget/FollowButton" props={{ accountId }} />
      </Item>
    ))}
  </Wrapper>
);
