const FormBackground = styled.div`
background-color:white;
padding:10px;
padding-top:30px;
margin-top:40px;
margin-bottom:60px;
border-radius:10px;
  border: 2px solid #B6E8F7;
`;
State.init({
  eventName: "",
  location: "",
  description: "",
  date: null,
  isSingleDateEvent: false,
});

const Grid = styled.div`
    display: grid;
    grid-template-columns: 1fr;  // By default, 1 item per row
    gap: 16px;  // Gap between rows and columns

    @media (min-width: 1000px) {
        grid-template-columns: 1fr 1fr;  // 2 items per row if width is greater than 1000px
    }
`;
const Label = styled.p`
font-size: 16px;
 translate: 3px 8px;
font-style: normal;
font-weight: 500;
`;

const AllSteps = [
  {
    label: "Event Info",
    active: true,
  },
  {
    label: "Collect Info",
  },
  {
    label: "Tickets",
  },
  {
    label: "Review",
  },
];

const formContent = () => {
  return (
    <FormBackground>
      <div
        style={{
          width: "fit-content",
          margin: "auto",
          transform: "scale(0.8)",
          marginTop: -65,
        }}
      >
        <Widget src="harrydhillon.near/widget/Keypom.TicketLogo" />
      </div>
      <div
        style={{
          margin: "auto",
          display: "flex",
          alignItems: "center",
          width: "fit-content",
          justifyContent: "space-evently",
        }}
      >
        {AllSteps.map((item, idx) => (
          <div style={{ marginRight: 20 }}>
            <Widget
              src="harrydhillon.near/widget/Keypom.StepLabel"
              props={{
                stepNumber: idx + 1,
                label: item.label,
                active: item?.active,
              }}
            />
          </div>
        ))}
      </div>
      {props?.content && props.content()}
    </FormBackground>
  );
};

return (
  <Widget
    src="harrydhillon.near/widget/Keypom.TicketDropLayout"
    props={{
      content: formContent,
    }}
  />
);
