/**
 * Add error check for contract name
 *
 */
const mainnet = props.mainnet ?? true;
const contract_id = mainnet ? "mintbase1.near" : "mintspace2.testnet";
const func = "create_store";
const owner_id = context.accountId;

const base_uri = "https://arweave.net";
const reference = null;
const reference_hash = null;
const spec = "nft-1.0.0";
const name = props.name ?? null; // or change with state
const symbol_name = props.symbol_name ?? null;
const icon =
  "data:image/png;base64,";
// Arguments: {
//   "owner_id": "bool.testnet",
//   "metadata": {
//     "spec": "nft-1.0.0",
//     "name": "bos",
//     "symbol": "bos",
//     "icon": "data:image/png;base64,",
//     "base_uri": "https://arweave.net",
//     "reference": null,
//     "reference_hash": null
//   }
// }

initState({
  name: name,
  symbol_name: symbol_name,
  reference: reference,
  reference_hash: reference_hash,
  nameIsActive: false,
});

const onChangeName = (name) => {
  State.update({
    name,
  });
};

const onChangeSymbol = (symbol_name) => {
  State.update({
    symbol_name,
  });
};

const deployStore = () => {
  if (!(state.symbol_name && state.name)) {
    console.log("You need a sybol and name to deploy a storefront on mintbase");
    return;
  }
  const gas = 200000000000000; // check if gas is correct
  const deposit = 3500000000000000000000000; // change to 6.5 N
  Near.call([
    {
      contractName: contract_id,
      methodName: func,
      args: {
        owner_id: owner_id,
        metadata: {
          spec: spec,
          name: state.name,
          symbol: state.symbol_name,
          icon,
          icon,
          base_uri: base_uri,
          reference: state.reference,
          reference_hash: state.reference_hash,
        },
      },
      gas: gas,
      deposit: deposit,
    },
  ]);
};

function fetchData() {
  const response = fetch("https://graph.mintbase.xyz/mainnet", {
    method: "POST",
    headers: {
      "mb-api-key": "anon",
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      query: `query AllStoresQuery {
  nft_contracts {
    id
    symbol
  }
}`,
    }),
  });

  State.update({
    allNFTStores:
      response.body.data.nft_contracts &&
      response.body.data.nft_contracts.map((store) => store.id),
    allSymbols:
      response.body.data.nft_contracts &&
      response.body.data.nft_contracts.map((store) => store.symbol),
  });
  // console.log("data", state.allNFTStores);
}

fetchData();

if (!owner_id) {
  return <p>Please Login to Deploy a store</p>;
}
const Main = styled.div`
*{
  font-family: Helvetica Neue;
  }
  h1{
    margin: 2rem 0;
    font-weight: 600;
  }
  display: flex;
  flex-flow:column nowrap;
  justify-content:center;
  align-items: center;
  button {
    border: 1px solid black;
    border-radius: 0;
    color: white;
    background: black;
    text-align: center;
    display: flex;
    padding: 7px 20px;
    cursor: pointer;
    margin: 10px auto;
  }
  button:disabled {
    background: grey;
    border: grey;
    cursor: not-allowed;
  }
  button:hover {
    background: white;
    color: black;
    border-color: black;
  }

  .input, .name {
    display: flex;
    align-items:center;
    justify-content:space-between;;
    border-radius: 32px;
    flex-shrink: 0;
    height: 48px;
    width: 100%;
    border: 1px solid #B0B0B0;
    background: #F8F8F8;
    overflow: hidden;
    color: #B0B0B0;
    text-overflow: ellipsis;
    font-family: Helvetica Neue;
    font-size: 20px;
    font-style: normal;
    font-weight: 400;
    outline: none;
    padding: 0 1rem;
    line-height: 148%; /* 29.6px */
    margin-bottom: 1rem;
    p{
      margin: 0;
    }
}
    .danger{
      color: red;
      border-color: red;
    }
.ip_name{
  background:transparent;
  border: none;
  outline: none;
  color: inherit;
  flex:1;
}
`;
return (
  <Main>
    <div>
      <h1>
        Deploy Store on Mintbase as <i>{owner_id}</i>
      </h1>
      <div>
        Contract Name*:
        <div
          className={`name ${
            state.allNFTStores &&
            state.allNFTStores.includes(`${state.name}.${contract_id}`) &&
            "danger"
          }`}
        >
          <input
            type="text"
            onChange={(e) => onChangeName(e.target.value)}
            className="ip_name"
          />
          <p>.{contract_id}</p>
        </div>
      </div>
      <div>
        Symbol* (max 3 letters):
        <input
          type="text"
          onChange={(e) => onChangeSymbol(e.target.value)}
          className={`input ${state.symbol_name.length > 3 ? "danger" : ""}`}
        />
      </div>
      <button
        onClick={deployStore}
        disabled={
          !state.symbol_name ||
          state.symbol_name.length > 3 ||
          !state.name ||
          (state.allNFTStores &&
            state.allNFTStores.includes(`${state.name}.${contract_id}`))
        }
      >
        Deploy Store 3.5N
      </button>
    </div>
    <Widget src="mintbase.near/widget/PoweredByMintbase" />
  </Main>
);
