draft:
State.init({
  show: false,
});

const handleOnMouseEnter = () => {
  State.update({ show: true });
};
const handleOnMouseLeave = () => {
  State.update({ show: false });
};

const OverlayTriggerWrapper = styled.div`
  display: flex;
  border: 1px solid #db504a;
  max-width: 596px;
  min-height: 100px;
  position: relative;
  .OverlayTrigger {
    position: absolute;
    background: #fff;
    background: #db504a;
    border: 1px solid #db504a;
    width: 6px;
    height: 49px;
    right: -6px;
    top: 10px;
    border-radius: 0px 4px 4px 0px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
  }
`;

const TriggerShowLabel = styled.div`
  background: #db504a;
  width: 40px;
  height: 40px;
  border-radius: 0px 4px 4px 0px;
  z-index: 1070;
  cursor: pointer;
`;

const TriggerEar = styled.div`
  display: block;
  width: 2px;
  height: 2px;
  border-radius: 2px;
  background: #fff;
  position: relative;
  &:before {
    content: "";
    display: block;
    width: 2px;
    height: 2px;
    border-radius: 2px;
    background: #fff;
    position: absolute;
    top: 4px;
  }
  &:after {
    content: "";
    position: absolute;
    display: block;
    width: 2px;
    height: 2px;
    border-radius: 2px;
    background: #fff;
    bottom: 4px;
  }
`;

const overlay = (
  <TriggerShowLabel
    style={{ margin: "0px -7px" }}
    onMouseEnter={handleOnMouseEnter}
    onMouseLeave={handleOnMouseLeave}
  />
);

return (
  <OverlayTriggerWrapper>
    <OverlayTrigger
      show={state.show}
      trigger={["hover", "focus"]}
      delay={{ show: 250, hide: 300 }}
      placement="right"
      overlay={overlay}
    >
      <div
        style={{ opacity: state.show ? 0 : 1 }}
        className="OverlayTrigger"
        onMouseEnter={handleOnMouseEnter}
        onMouseLeave={handleOnMouseLeave}
      >
        <TriggerEar />
      </div>
    </OverlayTrigger>
  </OverlayTriggerWrapper>
);
