const accountId = props.accountId;
const shrinkToken = (value, decimals) => {
  return new Big(value || 0).div(new Big(10).pow(decimals || 24));
};

const expandToken = (value, decimals) => {
  return new Big(value).mul(new Big(10).pow(decimals));
};
const [amount, setAmount] = useState("");
const onStakeClick = () => {
  if (accountId) {
    const gas = 300 * 1000000000000;
    // TODO: doesn't support floats right now due to limitation of JS integers
    const deposit = Big(amount).mul(Big(10).pow(24)).toFixed(0);
    console.log(gas, deposit);
    Near.call(
      "lonk_validator.poolv1.near",
      "deposit_and_stake",
      {},
      gas,
      deposit
    );
  }
};

const account = fetch("https://rpc.mainnet.near.org", {
  method: "POST",
  headers: {
    "Content-Type": "application/json",
  },
  body: JSON.stringify({
    jsonrpc: "2.0",
    id: "dontcare",
    method: "query",
    params: {
      request_type: "view_account",
      finality: "final",
      account_id: accountId,
    },
  }),
});

const accountNum = JSON.parse(
  fetch("https://indexer.ref.finance/list-token-price").body
);
const getBalanceNum = (token_id) => {
  if (token_id && token_id.toLowerCase() === "near") {
    return accountNum["wrap.near"].price;
  }

  return accountNum[token_id].price;
};

const LONK_TOKEN_META = {
  decimals: 8,
  icon: "data:image/png;base64,",
  id: "token.lonkingnearbackto2024.near",
  name: "LONK fungible token",
  symbol: "LONK",
};
const NEAR_META = {
  decimals: 24,
  icon: "data:image/svg+xml;base64,",
  id: "NEAR",
  name: "NEAR",
  symbol: "NEAR",
};

const getBalance = (token_id, tokenMeta) => {
  let amount;

  if (!accountId) {
    return "0";
  }

  if (token_id === "NEAR") amount = account.body.result.amount;
  else {
    amount = Near.view(token_id, "ft_balance_of", {
      account_id: accountId,
    });
  }

  return !amount ? "0" : shrinkToken(amount, tokenMeta.decimals).toFixed();
};

const Container = styled.div`
    display:flex;
    justify-content:space-around;
    max-height:600px;
    padding:10px 0;
    align-items:center;
    border-top:2px solid #31cf34;
    @media screen and (max-width:768px){
        flex-direction:column;
        justify-content:center;
        gap:30px;
        max-height:900px;
        height:100%;
        padding:30px 0;
        width:100%;
    }
`;

const Content = styled.div`
    display:flex;
    flex-direction:row;
    justify-content:space-between;
`;
const Title = styled.div`
    display:flex;
    flex-direction:column;
    text-align:start;
    gap:2rem;
    @media screen and (max-width:768px){
        flex-direction:column;
        text-align:start;
        gap:0px;
    }
    .title{
        font-family: Lakki Reddy,cursive;
        font-size: 5em;
        text-shadow: 0 4px 4px #00000040;
        text-transform: lowercase;
        color:#31cf34;
    }
    .sub-title{
        font-family: Lakki Reddy,cursive;
        font-size: 6em;
        text-shadow: 0 4px 4px #00000040;
        text-transform: Upcase;
        color:#31cf34;
    }
`;
const Stake = styled.div`
    height:320px;
    width:450px;
    border:2px solid #31cf34;
    border-radius:10px;
    padding:25px 20px;
    display:flex;
    flex-direction:column;
    gap:4px;
    text-align:center;
    justify-content:center;
    align-items:center;
    @media screen and (max-width:768px){
        width:400px;
    }

    .title{
        width:70%;
        word-wrap: break-word;
        font-size:19px;
        color:#000000;
        font-weight:600;
    }
    .amount{
        font-size:3rem;
        text-decoration: underline;
        font-weight:600;
        color:#000000;
    }
    .sub-amount{
        font-size:14px;
        color:gray;
    }
    .available{
        font-size:15px;
        color:gray;
    }
    .footer{
        margin-top:10px;
        display:flex;
        flex-direction:row;
        justify-content:space-around;
        gap:10px;
        background:#31cf34;
        padding:10px 20px;
        width:100%;
        border-radius:20px;   
    }
    .avatar{
        margin-top:2px;
    }
    .title-footer{
        display:flex;
        flex-direction:column;
        gap:3px;
        text-align:start;
    }
    .button-stake{
        background:#ffffff;
        color:#31cf34;
        padding:10px 20px;
        outline:none;
        border:none;
        border-radius:10px;
        font-weight:600;
        cursor:pointer;
        &:hover{
          background:#8ce98e;
          color:#ffffff;
        }
    }
    .text{
        color:#ffffff;
        font-weight:600;
    }
    .swap{
      border:none;
      text-align:center;
      width:200px;
      outline:none;
      &::placeholder{
        text-align:center;
      }
      &:focus{
        border:none;
      }
    }
`;

const LONK_ICON =
  "data:image/png;base64,";
return (
  <>
    <Container>
      <Title>
        <div class="title">Stake</div>
        <div class="sub-title">$NEAR</div>
      </Title>
      <Stake>
        <div class="title">Stake $NEAR with LONK, secure the network</div>
        <div class="amount">
          <input
            onChange={(e) => setAmount(e.target.value)}
            class="swap"
            type="text"
            placeholder="0"
          />
        </div>
        <div class="sub-amount">
          ~{(Number(getBalanceNum("NEAR")) * amount).toFixed(2)} USD
        </div>
        <div class="available">
          Available: {Number(getBalance("NEAR", NEAR_META)).toFixed(2)}
        </div>
        <div class="footer">
          <div class="avatar">
            <img src={LONK_ICON} alt="logo" width="40" height="40" />
          </div>
          <div class="title-footer">
            <div class="text">lonk_validator.poolv1.near</div>
            <small>Uptime: 100% APY: 10,89%</small>
          </div>
          <div>
            <button
              onClick={onStakeClick}
              data-bs-toggle="modal"
              data-bs-target="#stake"
              class="button-stake"
            >
              Stake
            </button>
          </div>
        </div>
      </Stake>
    </Container>
    {!accountId && (
      <div
        class="modal fade"
        id="stake"
        tabindex="-1"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div class="modal-dialog modal-dialog-centered modal-dialog-scrollable">
          <div class="modal-content">
            <div
              class="modal-body d-flex justify-content-center align-items-center"
              style={{
                fontWeight: "600",
                backgroundColor: "#31cf34",
                color: "#fff",
                fontFamily: `${fondKod}`,
              }}
            >
              <Content>Please connect wallet!</Content>
            </div>
          </div>
        </div>
      </div>
    )}
  </>
);
