//---------------------------------------------------------------------------------------------------
const SearchInput = (
  <div
    style={{ background: themeColor?.sbt_area?.section_bg }}
    className="search p-4"
  >
    <div className="row">
      <div className="col-8 ">
        <Input
          onBlur={inputHandler}
