// ---------------------  pie chart ------------------------

const getPieProps = (data, [key, value], colors, chartOption) => {
  data = data || [];
  colors = colors || [];
  chartOption = chartOption || {};

  const dataFormat = data.map((s) => [s[key], s[value]]);
  const props = {
    data: dataFormat,
    colors: colors,
    chartOption: {
      title: "chart title",
      type: "pie",
      legend: false,
      connector: false,
      ...chartOption,
    },
    themeColor: { chart: themeColor.chart },
    spinnerColors: themeColor.spinnerColors,
  };
  return props;
};
const pieChartData = () => {
  return cardObjects
    .filter((o) => !o.offChart)
    .map((o) => {
      return {
        title: `${o.brand} ${o.afterbrand}`,
        value: Number.parseInt(o.description),
      };
    });
};
const PieChart = (
  <div
    className="col-lg-8"
    style={{
      boxShadow: "2px 10px 20px rgba(128, 117, 226, 0.2)",
    }}
  >
    <div
      style={{ backgroundColor: themeColor?.sbt_info?.card_bg }}
      className="shadow-sm rounded-4 "
    >
      <Widget
        src="lord1.near/widget/Pie-chart"
        props={getPieProps(
          pieChartData(),
          ["title", "value"],
          themeColor.chartColor,
          {
            title: "Social Actions",
            subtitle: "Social Actions",

            type: "pie",
            connector: true,
            legend: true,
          }
        )}
      />
    </div>
  </div>
);

