const Button0017 = styled.button`
  padding: 15px 25px;
  border: unset;
  border-radius: 15px;
  color: #212121;
  z-index: 1;
  background: #e8e8e8;
  position: relative;
  font-weight: ${(props) => props.fontweight ||"1000"};
  font-size: ${(props) => props.fontsize ||"17px"};
  box-shadow: 4px 8px 19px -3px rgba(0, 0, 0, 0.27);
  transition: all 250ms;
  overflow: hidden;

  &:before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 0;
    border-radius: 15px;
    background-color: #212121;
    z-index: -1;
    box-shadow: 4px 8px 19px -3px rgba(0, 0, 0, 0.27);
    transition: all 250ms;
  }

  &:hover {
    color: #e8e8e8;
    background: #212121;

    &:before {
      width: 100%;
    }
  }`;
const text = props.text || "Button0017";
const fontsize = props.fontsize;
const fontweight = props.fontweight;

return <Button0017 fontsize={fontsize} fontweight={fontweight}>{text}</Button0017>;
