let entries7 = Object.entries(tagCount7Days);
let day7PostSorted = entries7.sort((b, a) => a[1] - b[1]);

let totalItems1Day = 0;

// Sum the values in the day1PostSorted array
for (let i = 0; i < day1PostSorted.length; i++) {
