State.init({
  posts: []
});

const $ = VM.require("sdks.near/widget/Loader");
const {
  Buttons: { ButtonPrimary },
} = $("@mattb/frensly/commons");
const { LensSDK } = $("@sdks/lens-sdk");

LensSDK = new LensSDK(State, state);

if (!state.posts.length) {
  let query = `query ExplorePublications($request: ExplorePublicationRequest!) {
  explorePublications(request: $request) {
    items {
      ... on Post {
        ...PostFields
        __typename
      }
      ... on Quote {
        ...QuoteFields
        __typename
      }
      __typename
    }
    pageInfo {
      next
      __typename
    }
    __typename
  }
}

fragment PostFields on Post {
  id
  publishedOn {
    id
    __typename
  }
  isHidden
  isEncrypted
  momoka {
    proof
    __typename
  }
  txHash
  createdAt
  by {
    ...PublicationProfileFields
    __typename
  }
  stats {
    ...PublicationStatsFields
    __typename
  }
  operations {
    ...PublicationOperationFields
    __typename
  }
  metadata {
    ...AnyPublicationMetadataFields
    __typename
  }
  openActionModules {
    ...OpenActionModulesFields
    __typename
  }
  profilesMentioned {
    snapshotHandleMentioned {
      ...HandleInfoFields
      __typename
    }
    __typename
  }
  __typename
}

