  __typename
}

fragment QuoteBaseFields on Quote {
  id
  publishedOn {
    id
    __typename
  }
  isHidden
  isEncrypted
  momoka {
    proof
    __typename
  }
  txHash
  createdAt
  by {
    ...PublicationProfileFields
    __typename
  }
  stats {
