  openActionModules {
    ...OpenActionModulesFields
    __typename
  }
  root {
    ... on Post {
      ...PostFields
      __typename
    }
    ... on Quote {
      ...QuoteBaseFields
      __typename
    }
    __typename
  }
  profilesMentioned {
    snapshotHandleMentioned {
      ...HandleInfoFields
      __typename
    }
    __typename
  }
  __typename
}`;
  let request = {
    request: {
