const { tokenId } = props;
if (!tokenId) {
  throw "Missing tokenId";
}

const nearIcon =
  "data:image/svg+xml;base64,";
const metadata =
  tokenId === "near"
    ? {
        name: "Native NEAR",
        icon: nearIcon,
        symbol: "NEAR",
        decimals: 24,
      }
    : Near.view(tokenId, "ft_metadata");

const { bigToString, MutedDecimals } = VM.require(
  "mob.near/widget/Token.utils"
);

const name = metadata?.name || "";
const icon = tokenId === "wrap.near" ? nearIcon : metadata?.icon;
const symbol = metadata?.symbol || tokenId;

const Wrapper = styled.div`
.token-badge {
  vertical-align: middle;
  margin-right: 1rem;

  .token-name {
    color: #666;
  }

  img {
    width: 32px;
    height: 32px;
    border-radius: 16px;
    vertical-align: middle;
    margin-right: 0.5em;
  }
}
`;

return (
  <Wrapper>
    <div className="d-block token-badge">
      <div className="token-name text-truncate" title={name}>
        {name}
      </div>
      <div
        title={tokenId}
        className="text-nowrap overflow-hidden d-flex align-items-center"
      >
        {icon && <img src={icon} alt="Token Icon" />}
        <span className="font-monospace align-middle text-truncate">
          {symbol}
        </span>
      </div>
    </div>
  </Wrapper>
);
