const { tokenId, balance, usdBalance } = props;
if (!tokenId) {
  throw "Missing tokenId";
}

const bigBalance =
  balance === null || balance === undefined ? null : Big(balance);
const nearIcon =
  "data:image/svg+xml;base64,";

const metadata =
  tokenId === "near"
    ? {
        name: "Native NEAR",
        icon: nearIcon,
        symbol: "NEAR",
        decimals: 24,
      }
    : Near.view(tokenId, "ft_metadata");

const { bigToString, MutedDecimals } = VM.require(
  "mob.near/widget/Token.utils"
);

const adjustedBalance =
  !bigBalance || !metadata?.decimals
    ? bigBalance
    : bigBalance.div(Big(10).pow(metadata.decimals));

const name = metadata?.name || "";
const icon = tokenId === "wrap.near" ? nearIcon : metadata?.icon;
const symbol = metadata?.symbol || tokenId;

const Wrapper = styled.div`
display: flex;
padding: 0.5em;
flex-wrap: nowrap;
justify-content: space-between;
.token-badge {
  min-width: 0;
  vertical-align: middle;
  margin-right: 1rem;

  .token-name {
    color: #666;
  }

  img {
    width: 32px;
    height: 32px;
    border-radius: 16px;
    vertical-align: middle;
    margin-right: 0.5em;
  }
}
&:hover {
  background: rgba(0, 0, 0, 0.02)
}
`;

return (
  <Wrapper>
    <div className="d-inline-block token-badge flex-grow-1">
      <Link href={`/mob.near/widget/Tokens.Top?tokenId=${tokenId}`}>
        <div className="token-name text-truncate" title={name}>
          {name}
        </div>
        <div
          title={tokenId}
          className="text-nowrap overflow-hidden d-flex align-items-center"
        >
          {icon && <img src={icon} alt="Token Icon" />}
          <span className="font-monospace align-middle text-truncate text-dark">
            {symbol}
          </span>
        </div>
      </Link>
    </div>
    <div className="font-monospace fw-bold">
      <MutedDecimals
        value={bigToString(adjustedBalance ? adjustedBalance.toString() : null)}
      />
    </div>
    {Big(usdBalance || "0").gt(0) && (
      <div className="font-monospace fw-bold d-inline-flex ms-3">
        <span className="text-secondary">~$</span>
        <MutedDecimals value={bigToString(usdBalance)} />
      </div>
    )}
  </Wrapper>
);
