const sLuckyGem = styled.div`
    {
        --primary-color: #d43c86;
        --border-color: #cecc6c;
        display: flex;
        flex-flow: row wrap;
        padding: 30px;
        margin: 30px;
        max-width: 400px;
        font-family: 'Roboto';
        border: 0 none;
        border-radius: 20px;
        background: linear-gradient(90deg, #fffcf8 10%, #ffe9dc 100%);
        box-shadow: 0 2px 5px rgba(212, 60, 134, 0.5);
    }

    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local("roboto"),
            url(https://fonts.cdnfonts.com/s/12165/Roboto-Regular.woff) format("woff");
    }


    @keyframes lightMove {
        from {
            top: -90%;
            width: 150%;
            height: 200%;
        }
        to {
            top: -60%;
            width: 200%;
            height: 180%;
        }
    }

    @keyframes bubble1 {
        0%{
            opacity: 0.5;
        }
        50% {
            opacity: 1;
        }
        100% {
            top: 0%;
            opacity: 0;
        }
    }

    @keyframes priceGrow{
        to {
            transform: scale(1.05);
        }
    }
`;

const sOrbe = styled.div`
  position: relative;
  flex: 0 0 auto;
  width: 100px;
  height: 100px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: 'Roboto';
  background-color: var(--primary-color);
  border-radius: 50%;
  overflow: hidden;
  box-shadow: 0 2px 2px 1px rgba(0, 0, 0, 0.34);
`;

const sOrbeBack = styled.div`
    position: absolute;
    flex: 1 1 100%;
    top: -110%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(255,255,255,0) 20%, rgba(255,255,255,0.4) 40%, rgba(250,255,255,0) 60%);
    animation: lightMove 10s infinite alternate linear;
`;

const sOrbeBubble = styled.div`
    position: absolute;
    top: 80%;
    left: 25%;
    width: 3px;
    height: 3px;
    border-radius: 50%;
    opacity: 0;
    background-color: rgba(255, 255, 255, 0.5);
    animation: bubble1 3s infinite normal ease;
`;

const sPrize = styled.div`
    display: flex;
    flex-flow: column wrap;
    align-items: center;
    justify-content: center;
    flex: 1 1 auto;
    font-size: 22px;
`;

const sButtons = styled.div`
    flex: 0 0 100%;
    width: 100%;
    padding: 20px 0 0;
    text-align: center;
`;

const sActionButton = styled.div`
    padding: 15px;
    margin: 0;
    background-color: var(--primary-color);
    border: none;
    border-radius: 8px;
    box-shadow: none;
    font-size: 15px;
    color: #fff;
    cursor: pointer;
`;

const matchPrizeStyles = [
  {},
  {},
  {
    margin: "0 0 5px 0",
    padding: "10px",
    "border-radius": "15px",
    border: "1px solid #f88c49",
    animation: "priceGrow 0.3s 2 alternate linear",
  },
];

function Orbe() {
  return (
    <sOrbe>
      <sOrbeBack />
      <sOrbeBubble style={{ left: "25%" }} />
      <sOrbeBubble style={{ left: "40%", "animation-delay": "2.5s" }} />
      <sOrbeBubble style={{ left: "50%", "animation-delay": "0.75s" }} />
      <sOrbeBubble style={{ left: "60%", "animation-delay": "1.75s" }} />
      <sOrbeBubble style={{ left: "70%", "animation-delay": "0.45s" }} />

      <div class="orbe-hash">Tx9</div>
    </sOrbe>
  );
}

function PrizeValues({ prizes }) {
  return (
    <sPrize className="LuckyGem-prize">
      {prizes.length > 0 &&
        prizes.map((prize) => (
          <div style={matchPrizeStyles[prize.match - 1]}>
            Match {prize.match} - {prize.value}
          </div>
        ))}
    </sPrize>
  );
}

function ActionButton() {
  return (
    <sButtons>
      <sActionButton>CHOOSE YOUR GEM</sActionButton>
    </sButtons>
  );
}

const prizes = [
  { match: 3, value: 436.25 },
  { match: 2, value: 120.25 },
  { match: 1, value: "050.25" },
];

return (
  <sLuckyGem>
    <Orbe />
    <PrizeValues prizes={prizes} />
    <ActionButton />
  </sLuckyGem>
);
