const TokenImage = ({
  appUrl,
  src,
  alt,
  className,
  onLoad,
  onSetSrc,
}) => {
  const placeholder = `${appUrl}images/tokenplaceholder.svg`;

  const handleLoad = () => {
    if (onLoad) {
      onLoad();
