const H1 = styled.h1`
  font-size: 3rem;
  font-weight: 600;
  margin-bottom: 1em;
    @media screen and (max-width: 1200px){
      font-size: 2.5rem;
    }
    @media screen and (max-width: 900px){
      font-size: 2rem;
    }
    @media screen and (max-width: 540px){
      font-size: 1.5rem;
    }
`;
const OrgsSection = styled.div`
  width: 100%;
  max-width: 1440px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  grid-gap: 4em;
  gap: 4em;
  padding: 3em 0px;
  &>.right{
    flex: 60% 1;
    max-width: 36em;
    &>.orgs-desc{
    margin-bottom: 2em;
    font-size: 1.5rem;
      @media screen and (max-width: 900px){
        font-size: 1.2rem;
      }
      @media screen and (max-width: 540px){
        font-size: 1rem;
      }
    }
    &>.orgs-link{
      display: inline-flex;
      align-items: center;
      justify-content: center;
      grid-gap: 1em;
      gap: 1em;
      text-decoration: none;
      color: unset;
      cursor: pointer;
    }
      @media screen and (max-width: 900px){
      max-width: 100%;
      text-align: center;
      margin-bottom: 3em;
      }
  }
  &>.left{
  display: grid;
  grid-template-columns: repeat(2,1fr);
  grid-gap: 2em;
  gap: 2em;
  & .org {
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 2.5em;
    background: #fff;
    border: 1px solid transparent;
    border-right-color: transparent;
    border-right-style: solid;
    border-right-width: 1px;
    border-right: 3px solid #e5e8eb;
    box-shadow: -26px -18px 30px -12px rgba(93,134,194,.1),26px 18px 18px rgba(93,134,194,.1);
    position: relative;
    transition: all .3s;
    cursor: pointer;
    width: 13em;
    height: 12.5em;
    &:hover{
      border: 3px solid #e5e8eb;
    }
    @media screen and (max-width: 540px){
      width: 8em;
      height: 7.5em;
      border-radius: .7em;
    }
  }
  & .Orgs_icon {
  position: absolute;
  width: 5em;
  -webkit-animation: Orgs_rotate__2EIfH 12s linear infinite;
  animation: Orgs_rotate__2EIfH 12s linear infinite;
  animation-delay: 0s;
  top: -2em;
  left: -2em;
  z-index: -1;
  -webkit-animation-delay: 1s;
  animation-delay: 1s;
  @media screen and (max-width: 540px)
  width: 3.5em;
  }
  & .Orgs_logo{
    @media screen and (max-width: 540px){
      width: 4em;
    }
  }
  }
  }
  @media screen and (max-width: 900px){
  flex-direction: column;
  grid-gap: 2em;
  gap: 2em;
  }
  }
`;
const Accent = styled.span`
color: #9BB486;
`;
return (
  <>
    <OrgsSection>
      <div className="right">
        <H1>
          <span>Backed by</span>
          <Accent>NEAR's Best</Accent>
        </H1>
        <div className="orgs-desc">
          We are supported by the best in the NEAR Ecosystem
        </div>
        <a className="orgs-link" href="https://NEAReFi.org/partner">
          <div>Partner w/ Us</div>
          <img
            className="arrow"
            alt="arrow"
            width="10px"
            src="https://www.svgrepo.com/show/5465/right-arrow.svg"
            target="_blank"
            rel="noreferrer"
          />
        </a>
      </div>
      <div className="left">
        <a
          className="org"
          href="https://banyan.gg/"
          target="_blank"
          rel="noreferrer"
        >
          <img className="Orgs_logo" src="" alt="Banyan Collective" />
          <img
            className="Orgs_icon"
            src="https://www.genadrop.com/static/media/logo-celo-icon.d1fed266.png"
            alt=""
          />
        </a>
        <a
          className="org"
          href="https://near.foundation/"
          target="_blank"
          rel="noreferrer"
        >
          <img
            className="Orgs_logo"
            src="data:image/png;base64,"
            alt=""
          />
          <img
            className="Orgs_icon"
            src="https://www.genadrop.com/static/media/logo-near-icon.df006c52.png"
            alt=""
          />
        </a>
        <a
          className="org"
          href="https://www.minorityprogrammers.com/"
          target="_blank"
          rel="noreferrer"
        >
          <img
            className="Orgs_logo"
            src="https://www.genadrop.com/static/media/logo-mp.73b4202c.png"
            alt="MPA"
          />
          <img
            className="Orgs_icon"
            src="https://www.genadrop.com/static/media/logo-polygon-icon.e75cb119.png"
            alt=""
          />
        </a>
        <a
          className="org"
          href="https://www.neardc.org/"
          target="_blank"
          rel="noreferrer"
        >
          <img className="Orgs_logo" src="" alt="NEAR Digital Collective" />
          <img
            className="Orgs_icon"
            src="https://pbs.twimg.com/profile_images/1622941553839816707/nmf3MWw1_400x400.jpg"
            alt="NEAR DC Logo"
          />
        </a>
      </div>
    </OrgsSection>
  </>
);
