const accountId = props.accountId ?? context.accountId ?? "sking.near";
const options = props.options ?? {};
const href = props.href ?? `#/mob.near/widget/ProfilePage?accountId=${accountId}`;

const showHumanBadge = options.showHumanBadge ?? true;
const showImage = options.showImage ?? true;
const showSocialName = options.showSocialName ?? true;
const size = options.size ?? "md";
const shortenLength = options.shortenLength ?? 50;
const fontSize = options.fontSize;

let profile = Social.getr(`${accountId}/profile`);
if (profile) {
  State.init({ profile: profile });
}

let isHuman = false;
if (showHumanBadge) {
  const userSBTs = Near.view(
    "registry.i-am-human.near",
    "sbt_tokens_by_owner",
    {
      account: accountId,
    },
  );
  if (userSBTs) {
    userSBTs.forEach((sbt) => {
      if ("fractal.i-am-human.near" === sbt[0]) {
        isHuman = true;
      }
    });
  }
}

const A = styled.a`
  img {
    vertical-align: baseline;
  }
`;

function makeAccountIdShorter(accountId) {
  if (accountId.length > shortenLength) {
    return accountId.slice(0, shortenLength) + "...";
  }
  return accountId;
}

return (
  <A
    className="d-flex text-decoration-none align-items-center"
    href={href}
    target="_blank"
  >
    {showImage && (
      <Widget
        src="mob.near/widget/ProfileImage"
        props={{
          profile,
          accountId: accountId,
          className: "float-start d-inline-block me-2",
          imageClassName: "rounded-circle w-100 h-100",
          style: {
            width: size === "sm" ? 20 : 36,
            height: size === "sm" ? 20 : 36,
          },
        }}
      />
    )}
    <div
      className={
        size == "sm"
          ? "d-flex flex-row gap-1 align-items-center"
          : "d-flex flex-column"
      }
    >
      <p
        style={{
          fontWeight: "700",
          fontSize: fontSize ?? (size === "sm" ? 12 : 15),
          textDecotarion: "none",
          color: "#000",
        }}
        className="d-inline-flex align-items-center gap-1 m-0"
      >
        {showSocialName
          ? profile.name
            ? makeAccountIdShorter(profile.name)
            : makeAccountIdShorter(accountId)
          : makeAccountIdShorter(accountId)}

        {showHumanBadge && isHuman && (
          <img
            height={size === "sm" ? 15 : 20}
            width={size === "sm" ? 15 : 20}
            src="data:image/png;base64,"
            alt="Verified Human"
          />
        )}
      </p>
      {showSocialName && profile.name ? (
        <>
          <span
            style={{
              margin: "0",
              fontWeight: "700",
              fontSize: 18,
              textDecotarion: "none",
              color: "#000",
            }}
          >
            {size === "sm" ? "•" : ""}
          </span>
          <p
            style={{
              margin: "0",
              fontWeight: "500",
              textDecoration: "none",
              fontSize: fontSize ?? (size === "sm" ? 12 : 15),
              color: "#828688",
              opacity: 0.8,
              lineHeight: 0.85,
            }}
          >
            {makeAccountIdShorter(accountId)}
          </p>
        </>
      ) : null}
    </div>
  </A>
);
