const buttonStyle = {
  width: "50em",
  height: "10em",
  display: "block",
  background:
    "linear-gradient(15.09deg, rgba(0, 109, 210, 0.69) 9.51%, rgba(121, 178, 232, 0.69) 141.73%)",
  color: "#fff",
  border: "1px solid #fff",
  fontFamily: "Kadwa",
};

const rulesSrc = "ostolex.near/widget/Rules";
const gameSetupSrc = "ostolex.near/widget/GameSetup";

State.init({ nextPageSrc: null });

return (
  <>
    {state.nextPageSrc ? (
      <Widget src={state.nextPageSrc} props={props} />
    ) : (
      <div
        style={{
          width: "100%",
          height: "100%",
          "background-image":
            "url(https://bafybeieczh2ttcqjfs2ocltpzi55lrrvlh6nxt5hjlpy7pxzjtyw66bu74.ipfs.w3s.link/main_menu_animation.gif)",
          "background-size": "cover",
          "background-repeat": "no-repeat",
          "background-position": "center",
          position: "fixed",
          width: "100vw",
          height: "calc(-98px + 100vh)",
          top: "98px",
          left: 0,
        }}
      >
        <div
          class="container"
          style={{
            "min-height": "calc(-98px + 100vh)",
            "align-items": "center",
            display: "flex",
            "flex-direction": "column",
            "justify-content": "center",
          }}
        >
          <div class="row justify-content-center">
            <button
              onClick={() => {
                State.update({ nextPageSrc: gameSetupSrc });
              }}
              style={buttonStyle}
            >
              <h1>Play game</h1>
            </button>
          </div>
          <div class="row justify-content-center mt-3">
            <button
              onClick={() => {
                State.update({ nextPageSrc: rulesSrc });
              }}
              style={buttonStyle}
            >
              <h1>Learn rules</h1>
            </button>
          </div>
        </div>
      </div>
    )}
  </>
);
