const accountId = props.accountId ?? "efiz.near";
const daoId = props.daoId ?? "vibes.sputnik-dao.near";
const role = props.role ?? "vibee";

if (!accountId) {
  return "Please connect your NEAR wallet :)";
}

// need to check role if tastemaker

const policy = Near.view(daoId, "get_policy");
const daoBond = policy.proposal_bond;
// console.log("Proposal bound amount: " + policy.proposal_bond);
const handleProposal = () => {
  const gas = 200000000000000;
  const deposit = 100000000000000000000000;
  Near.call([
    {
      contractName: daoId,
      methodName: "add_proposal",
      args: {
        proposal: {
          description: "Reccomended as a vibee",
          kind: {
            AddMemberToRole: {
              member_id: accountId,
              role: role,
            },
          },
        },
      },
      gas: gas,
      deposit: daoBond,
    },
  ]);
};

const Wrapper = styled.div`
  .join-button {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    padding: 8px 16px;
    height: 32px;
    border-radius: 100px;
    font-weight: 600;
    font-size: 12px;
    line-height: 15px;
    text-align: center;
    cursor: pointer;
    background: #FBFCFD;
    border: 1px solid #D7DBDF;
    color: ${props.primary ? "#006ADC" : "#11181C"} !important;
    white-space: nowrap;

    &:hover,
    &:focus {
      background: #ECEDEE;
      text-decoration: none;
      outline: none;
    }

    i {
      display: inline-block;
      transform: rotate(90deg);
      color: #7E868C;
    }
  }
`;

return (
  <Wrapper>
    <button className="join-button" onClick={handleProposal}>
      reccomend as {role}
    </button>
  </Wrapper>
);
