const handleDrop = (e) => {
  const imageUrl = e.dataTransfer.getData("text");

  const image = document.createElement("img");
  image.src = imageUrl;

  document.body.appendChild(image);
};

const handleDragOver = (e) => {
  e.preventDefault();
};

return (
  <>
    <div onDrop="handleDrop()">
      <p>Drop your Kudos images here.</p>
    </div>
  </>
);
