const ownerId = "ramgor.near";
const onSave = props.onSave;

State.init({});

if (props.save) {
  onSave(state);
}

const Container = styled.div`
  width: 100%;
  display: flex;
  padding: 2rem 3rem;
  flex-direction: column;
  align-items: flex-start;
  gap: 2rem;
  border-radius: 0.5rem;
  border: 1px solid var(--ui-elements-light, #eceef0);
  background: var(--background-light, #fafafa);
`;

const HalfWidth = styled.div`
  width: 50%;

  @media screen and (max-width: 768px) {
    width: 100%;
  }
`;

const participantes = [];

function agregarParticipante() {
  const inputElement = document.getElementById("participantInput");
  const participante = inputElement.value;

  if (participante.trim() !== "") {
    participantes.push(participante);
    inputElement.value = "";

    // Actualizar la lista de participantes en la interfaz
    actualizarListaParticipantes();
  }
}

function actualizarListaParticipantes() {
  const listaElement = document.getElementById("participantList");
  listaElement.innerHTML = "";

  participantes.forEach((participante) => {
    const listItem = document.createElement("li");
    listItem.textContent = participante;
    listaElement.appendChild(listItem);
  });
}

function realizarSorteo() {
  if (participantes.length > 0) {
    const indiceGanador = Math.floor(Math.random() * participantes.length);
    const ganador = participantes[indiceGanador];

    // Mostrar al ganador en la interfaz
    const winnerElement = document.getElementById("winner");
    winnerElement.textContent = `¡El ganador es: ${ganador}!`;
  } else {
    alert("Agregue al menos un participante antes de realizar el sorteo.");
  }
}

const data = (
  <>
    <label for="participantInput">Ingrese un participante:</label>
    <input type="text" id="participantInput" />
    <button onclick="agregarParticipante()">Agregar Participante</button>

    <h2>Participantes:</h2>
    <ul id="participantList"></ul>

    <button onclick="realizarSorteo()">Realizar Sorteo</button>

    <h2>Ganador:</h2>
    <p id="winner"></p>
  </>
);

return <Container>{data}</Container>;
