const children = props.children;

const tailwindCssUrl =
  "https://cdn.jsdelivr.net/npm/tailwindcss@2.2/dist/tailwind.min.css";

State.init({
  theme: null,
  walletAddress: undefined,
  chainId: 0,
  balance: undefined,
});

const tailwindCss = fetch(tailwindCssUrl).body;

if (!tailwindCss) return "tailwind css not load";

if (!state.theme) {
  State.update({
    theme: styled.div`
      ${tailwindCss}
    `,
  });
}

State.update({
  walletAddress: Ethers.send("eth_requestAccounts", [])[0],
});

if (state.walletAddress) {
  Ethers.provider()
    .getNetwork()
    .then((chainIdData) => {
      if (chainIdData?.chainId) {
        State.update({ chainId: chainIdData.chainId });
      }
    });
}

if (![25925, 3501].includes(state.chainId) && state.chainId != 0) {
  return (
    <div>
      invalid chainId {state.chainId} {walletAddress}
    </div>
  );
}
const Theme = state.theme;

return (
  <Theme>
    <div className="flex">
      <div className="p-2 flex flex-wrap justify-center align-center items-center ">
        <div className="min-w-[50vw]">walletAddress: {state.walletAddress}</div>
        <div className="min-w-[50vw]">chainId: {state.chainId}</div>
        <div className="min-w-[50vw]">
          eth_requestAccounts: {Ethers.send("eth_requestAccounts", [])[0]}
          {JSON.stringify(Ethers.send("eth_requestAccounts", []))}
        </div>
        <div className="">
          <Web3Connect className="bg-red-400 p-2" />
        </div>
      </div>
    </div>
  </Theme>
);
