State.init({ greeting: "Have a great day", doc: [] });

const TextArea = styled.textarea`
  width: 60%;
  height: 10rem;
  border-radius: 10px;
  padding: 15px;
  position: absolute; /* Corrected typo: 'postion' to 'position' */
`;

const onChange = ({ target }) => {
  State.update({ greeting: target.value });
};

const Side = styled.div`
  width: 30%;
  height: 100vh;
  position: absolute;
  left: 0;
  border: solid 1px;
  border-radius: 10px;
`;

const createTopDoc = () => {
  const newDocObject = { id: Date.now() };
  State.set({ doc: [...State.get().doc, newDocObject] });
};

const state = State.get(); // Retrieve the state object

return (
  <>
    <Side>
      <button onClick={createTopDoc}>Create top document</button>
    </Side>
    <div>
      {state.greeting}
      {state.doc.map((item) => (
        <div key={item.id}>ID: {item.id}</div>
      ))}
    </div>
    {/*<TextArea onChange={onChange} />*/}
  </>
);
