const children = props.children;

const tailwindCssUrl =
  "https://cdnjs.cloudflare.com/ajax/libs/tailwindcss/2.2.19/tailwind.min.css";
// "https://cdn.jsdelivr.net/npm/tailwindcss@2.2.19/dist/tailwind.min.css";

const tailwindCss = fetch(tailwindCssUrl).body;

State.init({
  theme: null,
});

if (!tailwindCss) return "tailwind css not load";

if (!state.theme) {
  State.update({
    theme: styled.div`
        ${tailwindCss}
        .brand-green {
            background-color:#00E3B4;
        }
        .brand-dark-green {
            background-color:#00C8A0;
        }
        .primary-text {
            color: #262930;
        }
        .secondary-text {
            color: #656973;
        }
        .tertiary-text {
            color: #A3A3A3;
        }
        .green-text {
            color: #00C8A0;
        }
    `,
  });
}
const Theme = state.theme;
return (
  <Theme>
    {children}
    <div className="bg-emerald-300">sad</div>
    <div className="brand-green">sad</div>
    <div className="brand-dark-green secondary-text">asdasdasdasdsad</div>
    <div className="brand-dark-green primary-text">asdasdasdasdsad</div>
  </Theme>
);
