const Wrapper = styled.div`
    background: #1d1e1f;
    display: flex;
    flex-direction: column;
    max-width: 320px;
    font-family: 'Inter';
    color: #fff;
    border-radius: 8px;
    overflow: hidden;
`;
const SubWrapper = styled.div`
    display: flex;
    padding: 20px;
    flex-direction: column;
    justify-content: center;
    gap: 32px;
`;
const InputWrapper = styled.div`
    display: flex;
    flex-direction: column;
    gap: 4px;
    span {
        font-size: 12px;
    }
`;
const Input = styled.input`
    border: none;
    background: #333;
    outline: none;
    color: #fff;
    padding: 8px 12px;
    border-radius: 4px;
`;
const Button = styled.button`
    background: #8247E5;
    border-radius: 4px;
    border: none;
    color: #fff;
    padding: 8px 0;
    font-weight: 600;
    font-size: 14px;
`;

const Tab = styled.div`
    display: flex;
    gap: 1px;
`;

const TabItem = styled.div`
    flex:1;
    padding: 16px 24px;
    background: #333;
    font-size: 14px;
    cursor: pointer;
    &:hover {
        background: #1d1e1f;
    }
`;

State.init({
  isDeposit: false,
});

const { token0, token1 } = props.pair;

const { isDeposit } = state;

return (
  <Wrapper>
    <Tab>
      <TabItem>Deposit</TabItem>
      <TabItem>Withdraw</TabItem>
    </Tab>
    {isDeposit ? (
      <SubWrapper>
        <InputWrapper>
          <span>Amount of {token0}</span>
          <Input />
        </InputWrapper>
        <InputWrapper>
          <span>Amount of {token1}</span>
          <Input />
        </InputWrapper>
        <Button> Deposit </Button>
      </SubWrapper>
    ) : (
      <SubWrapper>
        <InputWrapper>
          <span>
            Amount of {token0}-{token1}
          </span>
          <Input />
        </InputWrapper>
        <Button> Withdraw </Button>
      </SubWrapper>
    )}
  </Wrapper>
);
