const getNft = async () => {
  const response = await fetch("https://linea-nft.azurewebsites.net/api/nft");
  const datas = await response.json();
  setNfts(datas);
};

return (
  <div>
    <div className="filter">
      {" "}
      <span>Filter by owner : </span>
      <input
        onChange={(ev) => setOwner(ev.target.value)}
        value={owner}
        type="text"
        placeholder="owner"
      ></input>
      <button onClick={connect}>Connect Metamask</button>
    </div>
    <div className="home">
      {nfts?.tokens?.map((nft, index) => (
        <div className="nft-card" key={index}>
          <AsyncImage {...nft} />

          <div className="description info">
            <span title={nft.tokenId}>
              Token Id :{" "}
              {nft.tokenId?.substring(0, 30) +
                (nft.tokenId?.length > 30 ? "..." : "")}
            </span>
            <span title={nft.address}>
              Address :{" "}
              {nft.address?.substring(0, 30) +
                (nft.address?.length > 30 ? "..." : "")}
            </span>
            <span title={nft.owner}>
              Owner :{" "}
              {nft.owner?.substring(0, 30) +
                (nft.owner?.length > 30 ? "..." : "")}
            </span>
          </div>
          <MetaDescription {...nft} />
        </div>
      ))}
      {nfts?.total === 0 && <div>No nft founds for these criterias</div>}
    </div>
  </div>
);
