State.init({
  count: 0,
});

const increment = () => {
  state.count++;
  State.update();
};

const decrement = () => {
  state.count--;
  State.update();
};

const resetCount = () => {
  state.count = 0;
  State.update();
};

const content = (
  <div class="flex flex-col gap-y-4 items-center justify-center">
    <p>Count is {state.count}</p>
    <div class="flex gap-x-4">
      <button
        onClick={increment}
        class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded text-lg"
      >
        +
      </button>
      <button
        onClick={decrement}
        class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded text-lg"
      >
        -
      </button>
      <button
        onClick={resetCount}
        class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Reset Count
      </button>
    </div>
  </div>
);

return (
  <>
    <Widget
      src="igris.near/widget/TailwindWrapper"
      props={{
        children: content,
      }}
    />
  </>
);
