const buttonStyled = `
  background: rgba(255, 255, 255, 0.5);
  border: none;
  border-radius: 24px;
  font-size: 0.8rem;
  text-transform: uppercase;
  list-style: none;
  color: white;
  &:hover {
  background: rgba(255, 255, 255, 0.8);
  }
  &:disabled {
    color: black;
    background: rgba(255, 255, 255, 0.25);
  }
`;

const Theme = styled.div`
  box-sizing: border-box;
  color: white;
  padding: 0;
  margin: 0;
  width: 100%;
  height: 100%;
  padding-top: 64px;
  padding-bottom: 64px;
  margin: 0 auto;
  background: rgb(0,0,0);
  background: linear-gradient(135deg, rgba(0,0,0,1) 50%, rgba(130,71,229,1) 100%);

  > .logo-wrap {
    text-align: center;
    margin-bottom: 16px;
    > h1 {
      color: white;
      margin-top: 16px;
    }
    > h2 {
      color: rgba(255, 255, 255, 0.8);
      font-size: 1.4rem;
    }
  }


  @media only screen and (min-device-width: 375px) and (orientation: portrait) {
    padding: 16px;
    padding-top: 90px !important;
    min-height: 100vh !important;
    min-width: 100vw !important;
    overflow: hidden !important;
    overflow-y: scroll !important;
    background: black !important;
    .grid {
      flex-direction: row-reverse !important;
      flex-flow: wrap-reverse !important;
    }
  }


  @media only screen and (min-device-width: 375px) {
    .apps {
      max-width: 350px;
    }
  }

  .swap-main-column {
    padding-top: 0 !important;
    margin-top: -16px !important;
  }

  .bridge-abs {
    ${buttonStyled}
    position: absolute;
    top: 0;
    right: 0;
    z-index: 99999;
  }

  .connect-web3 {
    background: white;
    color: #44F;
    border-color: white;
    &:hover {
      background: #ddd;
      border-color: #bbb;
      color: black;
      color: #00F;
    }
  }

  .grid {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;
    flex-flow: row wrap;
    width: 100%;
    min-width: 375px;
  }

  .logo-grid {
    text-align: center;
    position: relative;
    cursor: pointer;
    > .logo {
      margin-right: 32px;
    }
    > .bridge-button {
      ${buttonStyled}
      padding: 6px 12px;
      &:disabled {
        opacity: 0 !important;
      }
    }
    > p {
      font-size: 0.8rem;
      color: rgba(255, 255, 255, 0.8);
      margin-bottom: 32px;
      margin-bottom: 64px;
    }
    > span {
      position: absolute;
      top: -32px;
      font-weight: bold;
      font-size: 2.5rem;
    }
  }

  .center {
    > div {
      display: flex;
      align-items: flex-start;
      justify-content: center;
      flex-wrap: wrap;
    }
  }

  .apps {
    text-align: left;

    .grid {
      display: flex;
      align-items: center;
      justify-content: space-between;
      width: 100%;
      padding-right: 16px;

      > .logo {
        width: 260px;
        height: 40px;
        margin: 0;
      }

    }

    > div {
      display: flex;
      flex-wrap: wrap;

    > div {
      width: 150px;
      height: 150px;
      margin: 8px;
      margin-bottom: 64px;
      text-align: center;
      > img {
        width: 100px;
        border-radius: 50%;
      }
      >h1 {
        margin: 16px 0;
        font-size: 1rem;
      }
      > button {
        ${buttonStyled}
      }
    }
  }

  .component {
    margin-top: 1.5rem;
  }
`;

State.init({
  bridge: true,
  component: null,
});

const disableApps = state.sender === "";

const sender = Ethers.send("eth_requestAccounts", [])[0] || "";

if (sender) {
  Ethers.provider()
    .getNetwork()
    .then(({ chainId }) => {
      State.update({ chainId });
    });
}

if (state.sender === undefined) {
  State.update({ sender: Ethers.send("eth_requestAccounts", [])[0] || "" });
}

if (state.sender === "") {
  State.update({ sender: Ethers.send("eth_requestAccounts", [])[0] || "" });
}

const { bridge, component } = state;

const setComponent = (component) => {
  let update = { component, bridge: null };
  if (component === "bridge") update = { component: null, bridge: true };
  State.update(update);
};

if (bridge !== true && (state.sender === "" || state.chainId !== 1101)) {
  return (
    <Theme>
      <div className="logo-grid">
        <div className="logo">{logo}</div>
      </div>

      <div className="logo-wrap">
        <h1>Bring Ethereum to everyone.</h1>
        <h2>Fast, cheap and secure.</h2>
        {!state.sender === undefined && (
          <Web3Connect
            connectLabel="Connect with Web3"
            className="connect-web3"
          />
        )}
        {state.sender !== "" && state.chainId !== 1011 && (
          <p>
            To proceed, please switch to the Polygon ZKEVM Network using your
            wallet.
          </p>
        )}
      </div>
    </Theme>
  );
}

return (
  <Theme>
    <div className="logo-grid" onClick={() => setComponent("bridge")}>
      <div className="logo">{logo}</div>
      {!bridge && (
        <>
          <span>←</span>
          <p>(back)</p>
        </>
      )}
    </div>

    <div className="logo-wrap">
      {bridge && (
        <>
          <h1>Bring Ethereum to everyone.</h1>
          <h2>Fast, cheap and secure.</h2>
        </>
      )}
    </div>

    <div className="grid">
      <div className="center">
        {bridge && (
          <div className="apps">
            <div>
              <div>
                <img
                  src={
                    "https://bafkreicivus3yfqvnefgprxf3lj5hzjpgiqe3gwld4bhxeyng4ndpeaxje.ipfs.nftstorage.link/"
                  }
                />
                <h1>Quickswap</h1>
                <button
                  disabled={disableApps || component === "quickswap"}
                  onClick={() => setComponent("quickswap")}
                >
                  DEX
                </button>
              </div>
              <div>
                <img
                  src={
                    "https://bafybeic6v34nkxhmro22tv2yoltsykniye2xlkgya6nxdqpxklu2bjn5me.ipfs.nftstorage.link/"
                  }
                />
                <h1>Gamma</h1>
                <button
                  disabled={disableApps || component === "gamma"}
                  onClick={() => setComponent("gamma")}
                >
                  Liquidity
                </button>
              </div>
              {false && (
                <div>
                  <img
                    src={
                      "https://bafkreiagr7ikurfd2jthfnrje6ckqsww5labskp3qy2vowed32j4amhdlq.ipfs.nftstorage.link/"
                    }
                  />
                  <h1>Aave</h1>
                  <button
                    disabled={disableApps || component === "aave"}
                    onClick={() => setComponent("aave")}
                  >
                    Lending Protocol
                  </button>
                </div>
              )}

              <div>
                <img
                  src={
                    "https://bafkreiafnfe7jzj3xxmpcxfkwob2fnfqdi66blmvpcrmcb3wnuwiaahtvi.ipfs.nftstorage.link/"
                  }
                />
                <h1>Pancake Swap</h1>
                <button
                  disabled={disableApps || component === "pancake"}
                  onClick={() => setComponent("pancake")}
                >
                  Dex
                </button>
              </div>
              <div>
                <img
                  src={
                    "https://bafkreiepbud73mssal3blwkj3pz3zyfloojhtw2kvxl7tli55tzrw6lmlu.ipfs.nftstorage.link/"
                  }
                />
                <h1>Balancer</h1>
                <button
                  disabled={disableApps || component === "balancer"}
                  onClick={() => setComponent("balancer")}
                >
                  Dex
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="component">
        <div className="center">
          {bridge === true && component === null ? (
            <Widget src="ciocan.near/widget/zkevm-bridge" />
          ) : (
            <>
              {component === "quickswap" && (
                <Widget
                  src="zavodil.near/widget/swap-zkevm"
                  props={{
                    dex: "QuickSwap",
                  }}
                />
              )}

              {component === "aave" && (
                <Widget src="aave-v3.near/widget/AAVE" />
              )}

              {component === "pancake" && (
                <Widget
                  src="zavodil.near/widget/swap-zkevm"
                  props={{
                    dex: "Pancake Swap",
                    forceNetwork: "ZKEVM",
                  }}
                />
              )}

              {component === "balancer" && (
                <Widget
                  src="zavodil.near/widget/swap-zkevm"
                  props={{
                    dex: "Balancer",
                    forceNetwork: "ZKEVM",
                  }}
                />
              )}

              {component === "gamma" && (
                <Widget src="mattlock.near/widget/gamma-zkevm-app" />
              )}
            </>
          )}
        </div>
      </div>
    </div>
  </Theme>
);
