const renderMention =
  props.renderMention ??
  props.onMention ??
  ((accountId) => (
    <span key={accountId} className="d-inline-flex" style={{ fontWeight: 500 }}>
      <Widget
        src="mob.near/widget/ProfileLine"
        props={{
          accountId: accountId.toLowerCase(),
          hideAccountId: true,
          tooltip: true,
        }}
      />
    </span>
  ));

const onHashtag = props.onHashtag;

const Wrapper = styled.div`
  word-break: break-word;
  p {
    white-space: pre-line;
  }
`;

const Embedded = styled.span`
  white-space: normal;
`;

const renderWidget =
  props.renderWidget ??
  props.onWidget ??
  // URL pattern: scheme://authority/path?query#fragment
  (({ url, scheme, authority, path, query }) => {
    // widget URL now allows "bos" and "near" schemes
    if (url && ["bos", "near"].includes(scheme) && authority && path) {
      const location = authority + path;
      const segments = location.split("/");
      if (segments && segments.length >= 3) {
        const src = segments.slice(segments.length - 3).join("/");
        const props = {
          ...{ markdown: props.text },
          ...(query ?? {}),
        };
        return (
          <Embedded className="embedded-widget">
            <Widget key={url} src={src} props={props} />
          </Embedded>
        );
      }
    } else if (url && url.includes("twitter.com")) {
      return (
        <>
          <blockquote class="twitter-tweet">
            <p lang="en" dir="ltr" />
            <a href={url}></a>
          </blockquote>{" "}
          <script
            async
            src="https://platform.twitter.com/widgets.js"
            charset="utf-8"
          ></script>
        </>
      );
    }
    // If not a valid widget URL, return the original URL
    return url;
  });

return (
  <Wrapper>
    <Markdown
      text={props.text}
      onMention={renderMention}
      onHashtag={onHashtag}
      onWidget={renderWidget}
    />
  </Wrapper>
);
