  "token.lonkingnearbackto2024.near": {
    name: "LONK",
    decimals: 8,
    image:
      "data:image/png;base64,",
  },
  "ftv2.nekotoken.near": {
    name: "NEKO",
    decimals: 24,
    image:
      "https://assets-global.website-files.com/627f75127980b632e08938a5/628668bb571921a4c96a08e3_niko.png",
  },
  "blackdragon.tkn.near": {
    name: "BLACKDRAGON",
    decimals: 24,
    image:
      "data:image/jpeg;base64,",
  },
